import { logger } from '@/lib/logger';
import { getStripe } from '@/lib/stripe';

export async function fetchStripeData() {
  const stripe = getStripe();
  if (!stripe) {
    return {
      monthlyRecurringRevenue: 0,
      totalRevenue: 0,
      failedPayments: 0,
      subscriptions: [],
      hasStripe: false,
    };
  }

  try {
    const stripeSubscriptions = await stripe.subscriptions.list({
      limit: 100,
      status: 'all',
    });

    let monthlyRecurringRevenue = 0;
    let totalRevenue = 0;
    let failedPayments = 0;
    const subscriptions: unknown[] = [];

    for (const sub of stripeSubscriptions.data) {
      const amount = sub.items.data[0]?.price?.unit_amount || 0;
      monthlyRecurringRevenue += amount;

      const periodStart = (sub as any).current_period_start; // justified
      const periodEnd = (sub as any).current_period_end; // justified
      if (typeof periodStart === 'number' && typeof periodEnd === 'number') {
        totalRevenue += (amount * (periodEnd - periodStart)) / (30 * 24 * 60 * 60);
      }

      subscriptions.push({
        id: sub.id,
        customer_email: sub.customer as string,
        status: sub.status,
        amount,
        created_at: new Date(sub.created * 1000).toISOString(),
      });

      if (sub.status === 'past_due' || sub.status === 'unpaid') {
        failedPayments++;
      }
    }

    return {
      monthlyRecurringRevenue,
      totalRevenue,
      failedPayments,
      subscriptions,
      hasStripe: true,
    };
  } catch (stripeError) {
    logger.warn('[Admin Billing] Stripe error (may not be configured):', stripeError);
    return {
      monthlyRecurringRevenue: 0,
      totalRevenue: 0,
      failedPayments: 0,
      subscriptions: [],
      hasStripe: true, // Stripe is configured but failed
    };
  }
}

import { standardAdminChecks } from '@/lib/admin-auth';
import { ApiErrorHandler } from '@/lib/api-error-handler';
import { logger } from '@/lib/logger';
import { NextRequest, NextResponse } from 'next/server';

/**
 * Severity priority order for sorting (Safety first)
 */
const SEVERITY_PRIORITY: Record<string, number> = {
  safety: 1,
  critical: 2,
  high: 3,
  medium: 4,
  low: 5,
};

/**
 * GET /api/admin/errors
 * List error logs with pagination and filters
 */
export async function GET(request: NextRequest) {
  try {
    const { supabase, error } = await standardAdminChecks(request);
    if (error) return error;
    if (!supabase) throw new Error('Unexpected database state');

    const { searchParams } = new URL(request.url);
    const page = parseInt(searchParams.get('page') || '1', 10);
    const pageSize = parseInt(searchParams.get('pageSize') || '20', 10);
    const search = searchParams.get('search') || '';
    const severity = searchParams.get('severity');
    const status = searchParams.get('status');
    const category = searchParams.get('category');

    let query = supabase.from('admin_error_logs').select('*', { count: 'exact' });

    // Apply filters
    if (search) {
      query = query.or(`error_message.ilike.%${search}%,endpoint.ilike.%${search}%`);
    }
    if (severity) {
      query = query.eq('severity', severity);
    }
    if (status) {
      query = query.eq('status', status);
    }
    if (category) {
      query = query.eq('category', category);
    }

    // Get all errors first (for sorting by severity priority)
    const { data: allErrors, error: fetchError, count } = await query;

    if (fetchError) {
      logger.error('[Admin Errors API] Database error:', {
        error: fetchError.message,
        context: { endpoint: '/api/admin/errors', method: 'GET' },
      });

      return NextResponse.json(ApiErrorHandler.fromSupabaseError(fetchError, 500), { status: 500 });
    }

    // Sort by severity priority (Safety first) then by date
    const sortedErrors = (allErrors || []).sort((a: any, b: any) => { // Justified: Supabase record typing
      const aPriority = SEVERITY_PRIORITY[a.severity || 'medium'] || 4;
      const bPriority = SEVERITY_PRIORITY[b.severity || 'medium'] || 4;
      if (aPriority !== bPriority) {
        return aPriority - bPriority;
      }
      // If same severity, sort by date (newest first)
      return new Date(b.created_at).getTime() - new Date(a.created_at).getTime();
    });

    // Apply pagination after sorting
    const from = (page - 1) * pageSize;
    const to = from + pageSize;
    const paginatedErrors = sortedErrors.slice(from, to);

    // Get counts by severity and status
    const severityCounts: Record<string, number> = {};
    const statusCounts: Record<string, number> = {};
    (allErrors || []).forEach((err: any) => { // Justified: Supabase record typing
      const sev = err.severity || 'medium';
      severityCounts[sev] = (severityCounts[sev] || 0) + 1;
      const stat = err.status || 'new';
      statusCounts[stat] = (statusCounts[stat] || 0) + 1;
    });

    const totalPages = Math.ceil((count || 0) / pageSize);

    return NextResponse.json({
      success: true,
      errors: paginatedErrors,
      page,
      pageSize,
      total: count || 0,
      totalPages,
      severityCounts,
      statusCounts,
    });
  } catch (error) {
    if (error instanceof NextResponse) {
      return error;
    }

    logger.error('[Admin Errors API] Unexpected error:', {
      error: error instanceof Error ? error.message : String(error),
      context: { endpoint: '/api/admin/errors', method: 'GET' },
    });

    return NextResponse.json(
      ApiErrorHandler.createError(
        process.env.NODE_ENV === 'development'
          ? error instanceof Error
            ? error.message
            : 'Unknown error'
          : 'Internal server error',
        'SERVER_ERROR',
        500,
      ),
      { status: 500 },
    );
  }
}

import { logAdminApiAction } from '@/lib/admin-audit';
import { requireAdmin } from '@/lib/admin-auth';
import { ApiErrorHandler } from '@/lib/api-error-handler';
import { logger } from '@/lib/logger';
import { supabaseAdmin } from '@/lib/supabase';
import { NextRequest, NextResponse } from 'next/server';
import { z } from 'zod';

const updateFlagSchema = z.object({
  enabled: z.boolean(),
  user_id: z.string().uuid().optional().nullable(),
});

// Helper to safely parse request body
async function safeParseBody(request: NextRequest) {
  try {
    return await request.json();
  } catch (err) {
    logger.warn('[Admin Features API] Failed to parse request body:', {
      error: err instanceof Error ? err.message : String(err),
    });
    return {};
  }
}

// Helper to apply user filter to query
function applyUserFilter(query: any, userId: string | null | undefined) { // justified: Supabase QueryBuilder type is complex
  if (userId !== undefined && userId !== null) {
    return query.eq('user_id', userId);
  }
  return query.is('user_id', null);
}

/**
 * PUT /api/admin/features/[flag]
 * Update a feature flag
 */
export async function PUT(request: NextRequest, context: { params: Promise<{ flag: string }> }) {
  try {
    const adminUser = await requireAdmin(request);
    const { flag } = await context.params;

    if (!supabaseAdmin) {
      return NextResponse.json(
        ApiErrorHandler.createError('Database connection not available', 'DATABASE_ERROR', 500),
        { status: 500 },
      );
    }

    const body = await request.json();
    const validated = updateFlagSchema.parse(body);

    let query = supabaseAdmin
      .from('feature_flags')
      .update({
        enabled: validated.enabled,
        updated_at: new Date().toISOString(),
      })
      .eq('flag_key', flag);

    query = applyUserFilter(query, validated.user_id);

    const { data: flagData, error } = await query.select().single();

    if (error) {
      logger.error('[Admin Features API] Database error updating flag:', {
        error: error.message,
        context: { endpoint: `/api/admin/features/${flag}`, method: 'PUT' },
      });

      return NextResponse.json(
        ApiErrorHandler.createError('Failed to update feature flag', 'DATABASE_ERROR', 500, {
          supabaseError: error.message,
        }),
        { status: 500 },
      );
    }

    // Log admin action
    await logAdminApiAction(adminUser, 'update_feature_flag', request, {
      target_type: 'feature_flag',
      target_id: flagData.id,
      details: { flag_key: flag, enabled: validated.enabled },
    });

    return NextResponse.json({
      success: true,
      flag: flagData,
    });
  } catch (error) {
    if (error instanceof NextResponse) {
      return error;
    }

    if (error instanceof z.ZodError) {
      return NextResponse.json(
        ApiErrorHandler.createError('Invalid request data', 'VALIDATION_ERROR', 400, error.issues),
        { status: 400 },
      );
    }

    logger.error('[Admin Features API] Unexpected error:', {
      error: error instanceof Error ? error.message : String(error),
      context: { endpoint: '/api/admin/features/[flag]', method: 'PUT' },
    });

    return NextResponse.json(
      ApiErrorHandler.createError(
        process.env.NODE_ENV === 'development'
          ? error instanceof Error
            ? error.message
            : 'Unknown error'
          : 'Internal server error',
        'SERVER_ERROR',
        500,
      ),
      { status: 500 },
    );
  }
}

/**
 * DELETE /api/admin/features/[flag]
 * Delete a feature flag
 */
export async function DELETE(request: NextRequest, context: { params: Promise<{ flag: string }> }) {
  try {
    const adminUser = await requireAdmin(request);
    const { flag } = await context.params;

    if (!supabaseAdmin) {
      return NextResponse.json(
        ApiErrorHandler.createError('Database connection not available', 'DATABASE_ERROR', 500),
        { status: 500 },
      );
    }

    const body = await safeParseBody(request);
    const userId = (body as { user_id?: string | null }).user_id || null;

    let query = supabaseAdmin.from('feature_flags').delete().eq('flag_key', flag);
    query = applyUserFilter(query, userId);

    const { error } = await query;

    if (error) {
      logger.error('[Admin Features API] Database error deleting flag:', {
        error: error.message,
        context: { endpoint: `/api/admin/features/${flag}`, method: 'DELETE' },
      });

      return NextResponse.json(
        ApiErrorHandler.createError('Failed to update feature flag', 'DATABASE_ERROR', 500, {
          supabaseError: error.message,
        }),
        { status: 500 },
      );
    }

    // Log admin action
    await logAdminApiAction(adminUser, 'delete_feature_flag', request, {
      target_type: 'feature_flag',
      target_id: flag,
      details: { flag_key: flag },
    });

    return NextResponse.json({
      success: true,
      message: 'Feature flag deleted successfully',
    });
  } catch (error) {
    if (error instanceof NextResponse) {
      return error;
    }

    logger.error('[Admin Features API] Unexpected error:', {
      error: error instanceof Error ? error.message : String(error),
      context: { endpoint: '/api/admin/features/[flag]', method: 'DELETE' },
    });

    return NextResponse.json(
      ApiErrorHandler.createError(
        process.env.NODE_ENV === 'development'
          ? error instanceof Error
            ? error.message
            : 'Unknown error'
          : 'Internal server error',
        'SERVER_ERROR',
        500,
      ),
      { status: 500 },
    );
  }
}

/**
 * Performance Insights AI API Endpoint
 *
 * Generates AI-powered performance insights with fallback to rule-based logic
 */

import type { PerformanceItem } from '@/app/webapp/performance/types';
import { generateAIResponse } from '@/lib/ai/ai-service';
import {
    buildPerformanceInsightsPrompt,
    parsePerformanceInsightsResponse,
} from '@/lib/ai/prompts/performance-insights';
import { ApiErrorHandler } from '@/lib/api-error-handler';
import { logger } from '@/lib/logger';
import { NextRequest, NextResponse } from 'next/server';
import { z } from 'zod';

const performanceInsightsSchema = z.object({
  performanceItems: z.array(z.any()).min(1, 'performanceItems array is required'),
  performanceScore: z.number(),
  countryCode: z.string().optional(),
});

interface InsightResult {
  insights: any[]; // justified: dynamic AI response
  source: 'ai' | 'fallback';
  cached?: boolean;
}

async function getAIPerformanceInsights(
  performanceItems: PerformanceItem[],
  performanceScore: number,
  countryCode: string = 'AU',
): Promise<InsightResult> {
  try {
    const prompt = buildPerformanceInsightsPrompt(performanceItems, performanceScore);
    const aiResponse = await generateAIResponse(
      [
        {
          role: 'user',
          content: prompt,
        },
      ],
      countryCode,
      {
        temperature: 0.7,
        maxTokens: 2000,
        useCache: true,
        cacheTTL: 60 * 60 * 1000, // 1 hour cache
      },
    );

    if (aiResponse.content && !aiResponse.error) {
      const insights = parsePerformanceInsightsResponse(aiResponse.content, performanceItems);
      if (insights.length > 0) {
        return {
          insights,
          source: 'ai',
          cached: aiResponse.cached,
        };
      }
    }
  } catch (aiError) {
    logger.warn('[AI Performance Insights] AI generation failed, using fallback:', {
      error: aiError instanceof Error ? aiError.message : String(aiError),
    });
  }

  return {
    insights: [],
    source: 'fallback',
  };
}

// Helper to safely parse request body
async function safeParseBody(request: NextRequest) {
  try {
    return await request.json();
  } catch (err) {
    logger.warn('[AI Performance Insights API] Failed to parse request body:', {
      error: err instanceof Error ? err.message : String(err),
    });
    return null;
  }
}

/**
 * POST /api/ai/performance-insights
 * Generate AI-powered performance insights with fallback to rule-based logic
 */
export async function POST(request: NextRequest) {
  try {
    const body = await safeParseBody(request);

    const validationResult = performanceInsightsSchema.safeParse(body || {});
    if (!validationResult.success) {
      return NextResponse.json(
        ApiErrorHandler.createError(
          validationResult.error.issues[0]?.message || 'Invalid request body',
          'VALIDATION_ERROR',
          400,
        ),
        { status: 400 },
      );
    }

    const { performanceItems, performanceScore, countryCode } = validationResult.data as {
      performanceItems: PerformanceItem[];
      performanceScore: number;
      countryCode?: string;
    };

    const result = await getAIPerformanceInsights(
      performanceItems,
      performanceScore,
      countryCode || 'AU',
    );

    return NextResponse.json(result);
  } catch (error) {
    logger.error('[AI Performance Insights] Unexpected error:', error);
    return NextResponse.json(
      ApiErrorHandler.createError('Failed to generate performance insights', 'INTERNAL_ERROR', 500),
      { status: 500 },
    );
  }
}

/**
 * Allergen Cross-Reference API Endpoint
 * GET /api/allergens/[code]/dishes
 * Returns all dishes containing a specific allergen
 */

import { aggregateDishAllergens } from '@/lib/allergens/allergen-aggregation';
import { ApiErrorHandler } from '@/lib/api-error-handler';
import { logger } from '@/lib/logger';
import { supabaseAdmin } from '@/lib/supabase';
import { NextRequest, NextResponse } from 'next/server';

async function getDishAllergensWithFallback(dish: any /* justified: complex DB record */): Promise<string[]> {
  const cachedAllergens = dish.allergens as string[] | null | undefined;
  if (cachedAllergens && cachedAllergens.length > 0) {
    return cachedAllergens;
  }

  try {
    return await aggregateDishAllergens(dish.id);
  } catch (err) {
    logger.warn('[Allergen Cross-Reference API] Error aggregating dish allergens:', {
      dishId: dish.id,
      error: err instanceof Error ? err.message : String(err),
    });
    return [];
  }
}

/**
 * Gets all dishes containing a specific allergen.
 */
export async function GET(_request: NextRequest, context: { params: Promise<{ code: string }> }) {
  try {
    const { code } = await context.params;

    if (!supabaseAdmin) {
      return NextResponse.json(
        ApiErrorHandler.createError('Database connection not available', 'DATABASE_ERROR', 500),
        { status: 500 },
      );
    }

    if (!code) {
      return NextResponse.json(
        ApiErrorHandler.createError('Allergen code is required', 'VALIDATION_ERROR', 400),
        { status: 400 },
      );
    }

    // Fetch all dishes
    const { data: dishes, error: fetchError } = await supabaseAdmin
      .from('dishes')
      .select('id, dish_name, allergens')
      .order('dish_name');

    if (fetchError && fetchError.code !== '42P01') {
      logger.error('[Allergen Cross-Reference API] Error fetching dishes:', {
        allergenCode: code,
        error: fetchError.message,
      });
      return NextResponse.json(
        ApiErrorHandler.createError('Failed to fetch dishes', 'DATABASE_ERROR', 500),
        { status: 500 },
      );
    }

    if (!dishes || dishes.length === 0) {
      return NextResponse.json({
        success: true,
        data: { allergen_code: code, dishes: [], count: 0 },
      });
    }

    // Aggregate allergens for all dishes
    const dishesWithAllergens = await Promise.all(
      dishes.map(async dish => ({
        id: dish.id,
        dish_name: dish.dish_name,
        allergens: await getDishAllergensWithFallback(dish),
      })),
    );

    // Filter by the requested allergen code
    const filteredDishes = dishesWithAllergens.filter(dish => dish.allergens.includes(code));

    return NextResponse.json({
      success: true,
      data: {
        allergen_code: code,
        dishes: filteredDishes,
        count: filteredDishes.length,
      },
    });
  } catch (err) {
    logger.error('[Allergen Cross-Reference API] Unexpected error:', err);
    return NextResponse.json(
      ApiErrorHandler.createError(
        'Failed to fetch dishes with allergen',
        'SERVER_ERROR',
        500,
        err instanceof Error ? err.message : String(err),
      ),
      { status: 500 },
    );
  }
}

/**
 * POST /api/backup/create
 * Create a manual backup of user data.
 */

import { ApiErrorHandler } from '@/lib/api-error-handler';
import { requireAuth } from '@/lib/auth0-api-helpers';
import { encryptBackup } from '@/lib/backup/encryption';
import { convertToSQL, exportUserData } from '@/lib/backup/export';
import { logger } from '@/lib/logger';
import { getAppError } from '@/lib/utils/error';

import { NextRequest, NextResponse } from 'next/server';
import { z } from 'zod';
import { storeBackupMetadata } from './helpers/storeMetadata';

const createBackupSchema = z.object({
  format: z.enum(['json', 'sql', 'encrypted']).optional().default('json'),
  encryptionMode: z.enum(['user-password', 'prepflow-only']).optional(),
  password: z.string().optional(),
});

type BackupFormat = 'json' | 'sql' | 'encrypted';

interface ProcessedBackup {
  content: string | Uint8Array;
  contentType: string;
  filename: string;
  fileSize: number;
}

async function prepareBackupContent(
  userId: string,
  format: BackupFormat,
  backupData: any, // justified: complex dynamic export data
  encryptionMode?: 'user-password' | 'prepflow-only',
  password?: string,
): Promise<ProcessedBackup> {
  if (format === 'encrypted') {
    const encrypted = await encryptBackup(backupData, {
      mode: encryptionMode!,
      password,
    });

    await storeBackupMetadata(userId, 'encrypted', encryptionMode, {
      recordCounts: backupData.metadata.recordCounts,
      fileSize: encrypted.size,
    });

    return {
      content: encrypted.data,
      contentType: 'application/octet-stream',
      filename: encrypted.filename,
      fileSize: encrypted.size,
    };
  }

  if (format === 'sql') {
    const sql = convertToSQL(backupData);
    const size = new TextEncoder().encode(sql).length;

    await storeBackupMetadata(userId, 'sql', undefined, {
      recordCounts: backupData.metadata.recordCounts,
      fileSize: size,
    });

    return {
      content: sql,
      contentType: 'text/sql',
      filename: `prepflow-backup-${new Date().toISOString().split('T')[0]}.sql`,
      fileSize: size,
    };
  }

  // Default: JSON
  const jsonContent = JSON.stringify(backupData, null, 2);
  const size = new TextEncoder().encode(jsonContent).length;

  await storeBackupMetadata(userId, 'json', undefined, {
    recordCounts: backupData.metadata.recordCounts,
    fileSize: size,
  });

  return {
    content: jsonContent,
    contentType: 'application/json',
    filename: `prepflow-backup-${new Date().toISOString().split('T')[0]}.json`,
    fileSize: size,
  };
}

/**
 * Creates a manual backup of user data.
 */
export async function POST(request: NextRequest) {
  try {
    const user = await requireAuth(request);
    const userId = user.email;

    let body: unknown;
    try {
      body = await request.json();
    } catch (_err) {
      return NextResponse.json(
        ApiErrorHandler.createError('Invalid JSON body', 'VALIDATION_ERROR', 400),
        { status: 400 },
      );
    }

    const validationResult = createBackupSchema.safeParse(body);
    if (!validationResult.success) {
      return NextResponse.json(
        ApiErrorHandler.createError(
          validationResult.error.issues[0]?.message || 'Invalid request body',
          'VALIDATION_ERROR',
          400,
        ),
        { status: 400 },
      );
    }

    const { format, encryptionMode, password } = validationResult.data;

    // Validate encryption options
    if (format === 'encrypted' && (!encryptionMode || (encryptionMode === 'user-password' && !password))) {
      return NextResponse.json(
        ApiErrorHandler.createError(
          'Missing encryption mode or password for encrypted backup',
          'VALIDATION_ERROR',
          400,
        ),
        { status: 400 },
      );
    }

    logger.info(`[Backup Create] Starting backup for user ${userId}, format: ${format}`);
    const backupData = await exportUserData(userId);

    const { content, contentType, filename, fileSize } = await prepareBackupContent(
      userId,
      format as BackupFormat,
      backupData,
      encryptionMode,
      password,
    );

    logger.info(`[Backup Create] Backup created successfully: ${filename}, size: ${fileSize} bytes`);

    const responseContent = format === 'encrypted' ? Buffer.from(content as Uint8Array) : (content as string);

    return new NextResponse(responseContent, {
      headers: {
        'Content-Type': contentType,
        'Content-Disposition': `attachment; filename="${filename}"`,
        'Content-Length': String(fileSize),
      },
    });
  } catch (error: unknown) {
    const appError = getAppError(error);
    logger.error('[Backup Create] Error creating backup:', {
      error: appError.message,
      code: appError.code,
      originalError: appError.originalError,
    });
    return NextResponse.json(
      ApiErrorHandler.createError('Failed to create backup', 'SERVER_ERROR', 500, appError.message),
      { status: 500 },
    );
  }
}

/**
 * Build usage map from recipe_ingredients rows
 *
 * @param {Array<{ingredient_id?: string; recipe_id?: string}>} rows - Recipe ingredient rows
 * @param {string} idField - Field name ('ingredient_id' or 'recipe_id')
 * @returns {Record<string, number>} Usage count by ID
 */
export function buildUsageMap(
  rows: Array<{ ingredient_id?: string; recipe_id?: string }>,
  idField: 'ingredient_id' | 'recipe_id',
): Record<string, number> {
  const usageMap: Record<string, number> = {};
  rows.forEach(r => {
    const row = r as Record<string, any>;
    const id = row[idField];
    if (!id) return;
    usageMap[id] = (usageMap[id] || 0) + 1;
  });
  return usageMap;
}

/**
 * Helper for processing ingredient allergens for a dish
 */

import { consolidateAllergens } from '@/lib/allergens/australian-allergens';
import { supabaseAdmin } from '@/lib/supabase';
import type { AllergenSource } from './processRecipeAllergens';

import { ApiErrorHandler } from '@/lib/api-error-handler';
import { IngredientRecord } from '../../../types';

interface RawIngredientJoin {
  id: string;
  ingredient_name: string;
  brand?: string;
  allergens?: string[];
  allergen_source?: {
    manual?: boolean;
    ai?: boolean;
  };
}

/**
 * Processes ingredient allergens for a dish
 *
 * @param {string} dishId - Dish ID
 * @param {Record<string, AllergenSource[]>} allergenSources - Allergen sources map to populate
 * @param {Set<string>} allAllergens - Set of all allergens to populate
 * @returns {Promise<void>}
 */
export async function processIngredientAllergens(
  dishId: string,
  allergenSources: Record<string, AllergenSource[]>,
  allAllergens: Set<string>,
): Promise<void> {
  if (!supabaseAdmin) {
    throw ApiErrorHandler.createError('Database connection not available', 'DATABASE_ERROR', 500);
  }

  const { data: dishIngredients, error: ingredientsError } = await supabaseAdmin
    .from('dish_ingredients')
    .select(
      `
      ingredient_id,
      quantity,
      unit,
      ingredients (
        id,
        ingredient_name,
        brand,
        allergens,
        allergen_source
      )
    `,
    )
    .eq('dish_id', dishId);

  if (ingredientsError) {
    // Log error but don't throw - caller handles empty result
    const { logger } = await import('@/lib/logger');
    logger.warn('[Process Ingredient Allergens] Error fetching dish ingredients:', {
      error: ingredientsError.message,
      code: ingredientsError.code,
      context: { dishId, operation: 'fetchDishIngredients' },
    });
    return;
  }

  if (!dishIngredients) {
    return;
  }

  dishIngredients.forEach(di => {
    const ingredient = di.ingredients as unknown as IngredientRecord | null;

    if (!ingredient) return;

    const allergens = (ingredient.allergens as string[]) || [];
    const consolidatedAllergens = consolidateAllergens(allergens);

    consolidatedAllergens.forEach(allergen => {
      if (typeof allergen === 'string' && allergen.length > 0) {
        allAllergens.add(allergen);

        if (!allergenSources[allergen]) {
          allergenSources[allergen] = [];
        }

        allergenSources[allergen].push({
          source_type: 'ingredient',
          source_id: ingredient.id,
          source_name: ingredient.brand
            ? `${ingredient.ingredient_name} (${ingredient.brand})`
            : ingredient.ingredient_name,
          quantity: di.quantity || undefined,
          unit: di.unit || undefined,
          allergen_source: ingredient.allergen_source || undefined,
        });
      }
    });
  });
}

/**
 * Helper for processing recipe allergens for a dish
 */

import { consolidateAllergens } from '@/lib/allergens/australian-allergens';
import { supabaseAdmin } from '@/lib/supabase';

import { ApiErrorHandler } from '@/lib/api-error-handler';
import { RecipeRecord } from '../../../types';

interface RawRecipeJoin {
  id: string;
  name?: string;
  recipe_name?: string;
  allergens?: string[];
}

export interface AllergenSource {
  source_type: 'recipe' | 'ingredient';
  source_id: string;
  source_name: string;
  quantity?: number;
  unit?: string;
  allergen_source?: {
    manual?: boolean;
    ai?: boolean;
  };
}

/**
 * Processes recipe allergens for a dish
 *
 * @param {string} dishId - Dish ID
 * @param {Record<string, AllergenSource[]>} allergenSources - Allergen sources map to populate
 * @param {Set<string>} allAllergens - Set of all allergens to populate
 * @returns {Promise<void>}
 */
export async function processRecipeAllergens(
  dishId: string,
  allergenSources: Record<string, AllergenSource[]>,
  allAllergens: Set<string>,
): Promise<void> {
  if (!supabaseAdmin) {
    throw ApiErrorHandler.createError('Database connection not available', 'DATABASE_ERROR', 500);
  }

  const { data: dishRecipes, error: recipesError } = await supabaseAdmin
    .from('dish_recipes')
    .select(
      `
      recipe_id,
      quantity,
      unit,
      recipes (
        id,
        name,
        recipe_name,
        allergens
      )
    `,
    )
    .eq('dish_id', dishId);

  if (recipesError) {
    // Log error but don't throw - caller handles empty result
    const { logger } = await import('@/lib/logger');
    logger.warn('[Process Recipe Allergens] Error fetching dish recipes:', {
      error: recipesError.message,
      code: recipesError.code,
      context: { dishId, operation: 'fetchDishRecipes' },
    });
    return;
  }

  if (!dishRecipes) {
    return;
  }

  dishRecipes.forEach(dr => {
    const recipe = dr.recipes as unknown as RecipeRecord | null;

    if (!recipe) return;

    const recipeName = recipe.recipe_name || recipe.name || 'Unknown Recipe';
    const allergens = (recipe.allergens as string[]) || [];
    const consolidatedAllergens = consolidateAllergens(allergens);

    consolidatedAllergens.forEach(allergen => {
      if (typeof allergen === 'string' && allergen.length > 0) {
        allAllergens.add(allergen);

        if (!allergenSources[allergen]) {
          allergenSources[allergen] = [];
        }

        allergenSources[allergen].push({
          source_type: 'recipe',
          source_id: recipe.id,
          source_name: recipeName,
          quantity: dr.quantity || undefined,
          unit: dr.unit || undefined,
        });
      }
    });
  });
}

/**
 * Aggregate ingredients from dish and recipes
 * Combines direct dish ingredients with recipe ingredients
 */

import { logger } from '@/lib/logger';
import { DishIngredient, DishRecipe } from '@/types/dish';

import { fetchDishIngredients } from '../../helpers/fetchDishIngredients';
import { fetchDishRecipes } from '../../helpers/fetchDishRecipes';
import { batchFetchRecipeIngredients } from './batchFetchRecipeIngredients';
import { extractInstructions } from './extractInstructions';

/**
 * Aggregate all ingredient names from dish and recipes
 *
 * @param {string} dishId - Dish ID
 * @returns {Promise<{ingredientNames: string[], recipeInstructions: string[]}>} Aggregated ingredient names and recipe instructions
 */
export async function aggregateDishIngredients(dishId: string): Promise<{
  ingredientNames: string[];
  recipeInstructions: string[];
}> {
  // Fetch dish ingredients
  let dishIngredients: DishIngredient[] = [];
  try {
    dishIngredients = await fetchDishIngredients(dishId);
  } catch (error) {
    logger.error('[Dish Image Generation] Failed to fetch dish ingredients:', {
      error: error instanceof Error ? error.message : String(error),
      dishId,
    });
  }

  // Fetch dish recipes
  let dishRecipes: DishRecipe[] = [];
  try {
    dishRecipes = await fetchDishRecipes(dishId);
  } catch (error) {
    logger.error('[Dish Image Generation] Failed to fetch dish recipes:', {
      error: error instanceof Error ? error.message : String(error),
      dishId,
    });
  }

  // Extract instructions
  const recipeInstructions = extractInstructions(dishRecipes);

  // Collect recipe IDs
  const recipeIds = dishRecipes.map(dr => dr.recipe_id || dr.id).filter((id): id is string => !!id);

  // Batch fetch recipe ingredients
  const recipeIngredientNamesSet = await batchFetchRecipeIngredients(recipeIds);

  // Aggregate all ingredient names
  const ingredientNamesSet = new Set<string>();

  // Add direct dish ingredients
  dishIngredients.forEach(di => {
    const ingredient = di.ingredient as Record<string, any> | undefined;
    if (ingredient) {
      const name = ingredient.ingredient_name || ingredient.name;
      if (name && typeof name === 'string') {
        ingredientNamesSet.add(name);
      }
    }
  });

  // Add ingredients from recipes
  recipeIngredientNamesSet.forEach(name => {
    ingredientNamesSet.add(name);
  });

  const ingredientNames = Array.from(ingredientNamesSet);

  logger.dev('[Dish Image Generation] Aggregated ingredients:', {
    dishId,
    directIngredientsCount: dishIngredients.length,
    recipesCount: dishRecipes.length,
    totalUniqueIngredients: ingredientNames.length,
  });

  return {
    ingredientNames,
    recipeInstructions,
  };
}

import { logger } from '@/lib/logger';
import { supabaseAdmin } from '@/lib/supabase';

interface RawAggregatedIngredient {
  ingredient_name?: string;
  name?: string;
}

export async function batchFetchRecipeIngredients(recipeIds: string[]): Promise<Set<string>> {
  const ingredientNamesSet = new Set<string>();
  if (recipeIds.length === 0 || !supabaseAdmin) return ingredientNamesSet;

  try {
    const { data: recipeIngredients, error } = await supabaseAdmin
      .from('recipe_ingredients')
      .select(
        `
        ingredients (
          ingredient_name
        )
      `,
      )
      .in('recipe_id', recipeIds);

    if (error) {
      logger.error('[Dish Image Generation] Failed to batch fetch recipe ingredients:', {
        error: error.message,
        recipeIdsCount: recipeIds.length,
      });
      return ingredientNamesSet;
    }

    if (recipeIngredients) {
      recipeIngredients.forEach((ri: any) => { // justified
        const ingredient = ri.ingredients;
        if (ingredient && typeof ingredient === 'object') {
          const rawIng = ingredient as unknown as RawAggregatedIngredient;
          const name = rawIng.ingredient_name || rawIng.name;
          if (name && typeof name === 'string') {
            ingredientNamesSet.add(name);
          }
        }
      });
    }
  } catch (error) {
    logger.error('[Dish Image Generation] Error in batch fetch recipe ingredients:', {
      error: error instanceof Error ? error.message : String(error),
    });
  }

  return ingredientNamesSet;
}

import { ApiErrorHandler } from '@/lib/api-error-handler';
import { logger } from '@/lib/logger';
import { supabaseAdmin } from '@/lib/supabase';
import type { PostgrestError } from '@supabase/supabase-js';
import { DishRelationIngredient } from '../../helpers/schemas';

// Define types for raw database rows
interface RawDishIngredientRow {
  id: string;
  dish_id: string;
  ingredient_id: string | null;
  quantity: number;
  unit: string;
}

interface RawIngredientRow {
  id: string;
  ingredient_name: string;
  cost_per_unit: number;
  cost_per_unit_incl_trim: number | null;
  trim_peel_waste_percentage: number | null;
  yield_percentage: number | null;
  unit: string;
  supplier: string | null;
  supplier_name?: string; // Sometimes aliased or joined
  category: string | null;
  brand: string | null;
  allergens: string[] | null;
  allergen_source: Record<string, unknown> | null;
}

/** Fetches ingredients for a dish with fallback logic */
/**
 * Helper to manually fetch ingredients and join them with dish_ingredients
 */
async function manualFetchAndJoinIngredients(dishId: string) {
  logger.dev('[Dishes API] Join returned empty, fetching dish_ingredients without join', {
    dishId,
  });

  if (!supabaseAdmin) {
    logger.error('[Dishes API] Database connection not available for manual fetch');
    return null;
  }

  const { data: rawDishIngredients, error: rawError } = await supabaseAdmin
    .from('dish_ingredients')
    .select('id, ingredient_id, quantity, unit')
    .eq('dish_id', dishId);

  if (rawError) {
    const rawPgError = rawError as PostgrestError;
    logger.warn('[Dishes API] Error fetching raw dish_ingredients:', {
      error: rawPgError.message,
      code: rawPgError.code,
    });
    return null;
  }

  if (!rawDishIngredients || rawDishIngredients.length === 0) {
    return [];
  }

  // Manually fetch ingredients and join
  const ingredientIds = rawDishIngredients
    .map(di => di.ingredient_id)
    .filter((id): id is string => Boolean(id));

  if (ingredientIds.length === 0) return [];

  const { data: ingredientsData, error: ingFetchError } = await supabaseAdmin
    .from('ingredients')
    .select(
      'id, ingredient_name, cost_per_unit, cost_per_unit_incl_trim, trim_peel_waste_percentage, yield_percentage, unit, supplier, category, brand, allergens, allergen_source',
    )
    .in('id', ingredientIds);

  if (ingFetchError) {
    logger.warn('[Dishes API] Error fetching ingredients for manual join:', {
      error: ingFetchError.message,
    });
    return null;
  }

  if (!ingredientsData) return null;

  // Create a map for quick lookup
  const ingredientsMap = new Map<string, RawIngredientRow>(
    (ingredientsData as RawIngredientRow[]).map(ing => [ing.id, ing]),
  );

  // Manually join the data
  const joinedData = rawDishIngredients.map(di => {
    const ingredient = di.ingredient_id ? ingredientsMap.get(di.ingredient_id) : undefined;
    return {
      ...di,
      ingredients: ingredient
        ? [
            {
              ...ingredient,
              supplier_name: ingredient.supplier || ingredient.supplier_name,
            },
          ]
        : [],
    };
  });

  logger.dev('[Dishes API] Manually joined dish ingredients', {
    dishId,
    count: joinedData.length,
    joinedCount: joinedData.filter(di => di.ingredients.length > 0).length,
  });

  return joinedData;
}

/**
 * Helper to retry fetching without the category column (schema drift coverage)
 */
async function retryFetchWithoutCategory(dishId: string) {
  if (!supabaseAdmin) {
    return { data: [], error: { message: 'DB not available' } as PostgrestError };
  }

  logger.warn('[Dishes API] Category column not found, retrying without category', {
    context: { endpoint: '/api/dishes/[id]', operation: 'GET', dishId },
  });

  const retryResult = await supabaseAdmin
    .from('dish_ingredients')
    .select(
      `
      id,
      dish_id,
      ingredient_id,
      quantity,
      unit,
      ingredients (
        id,
        ingredient_name,
        cost_per_unit,
        cost_per_unit_incl_trim,
        trim_peel_waste_percentage,
        yield_percentage,
        unit,
        supplier,
        brand,
        allergens,
        allergen_source
      )
    `,
    )
    .eq('dish_id', dishId);

  // Normalize the retry result to include category as null/undefined for type compatibility
  const retryData = (retryResult.data || []) as unknown as Record<string, unknown>[];
  const normalizedData = retryData.map(item => ({
    ...item,
    ingredients: Array.isArray(item.ingredients)
      ? item.ingredients.map((ing: Record<string, unknown>) => ({ ...ing, category: null }))
      : item.ingredients
        ? { ...(item.ingredients as Record<string, unknown>), category: null }
        : null,
  }));

  return { data: normalizedData, error: retryResult.error };
}

/** Fetches ingredients for a dish with fallback logic */
export async function fetchDishIngredients(dishId: string): Promise<DishRelationIngredient[]> {
  if (!supabaseAdmin) {
    logger.error('[Dishes API] Database connection not available for fetchDishIngredients');
    throw ApiErrorHandler.createError('Database connection not available', 'DATABASE_ERROR', 500);
  }

  // First, try to fetch with join to ingredients table
  let { data: dishIngredients, error: ingredientsError } = await supabaseAdmin
    .from('dish_ingredients')
    .select(
      `
      id,
      dish_id,
      ingredient_id,
      quantity,
      unit,
      ingredients (
        id,
        ingredient_name,
        cost_per_unit,
        cost_per_unit_incl_trim,
        trim_peel_waste_percentage,
        yield_percentage,
        unit,
        supplier,
        category,
        brand,
        allergens,
        allergen_source
      )
    `,
    )
    .eq('dish_id', dishId);

  // If join fails or returns empty but we know rows exist, fetch without join and manually join
  const pgError = ingredientsError as PostgrestError | null;
  if (
    (!dishIngredients || dishIngredients.length === 0) &&
    (!pgError || pgError.code !== '42703')
  ) {
    const manualResult = await manualFetchAndJoinIngredients(dishId);
    if (manualResult) {
      // @ts-expect-error - Complex nested type mismatches during migration we expect even if types are tricky
      dishIngredients = manualResult;
    }
  }

  // If category column doesn't exist, retry without it
  const retryError = ingredientsError as PostgrestError | null;
  if (retryError && retryError.code === '42703' && retryError.message?.includes('category')) {
    const { data, error } = await retryFetchWithoutCategory(dishId);
    // @ts-expect-error - Recursive type mismatches in ingredient graph
    dishIngredients = data;
    ingredientsError = error;
  }

  // Log error if still present (but don't fail the whole request)
  if (ingredientsError) {
    logger.warn('[Dishes API] Error fetching dish ingredients (non-fatal):', {
      error: ingredientsError.message,
      code: ingredientsError.code,
      context: { endpoint: '/api/dishes/[id]', operation: 'GET', dishId },
    });
  }

  // Filter out dish_ingredients entries where the ingredients relation is null (deleted ingredients)
  // Also map 'supplier' to 'supplier_name' to match frontend types
  const rawIngredients = (dishIngredients || []) as unknown as Record<string, unknown>[];

  const validDishIngredients = rawIngredients
    .filter(di => di.ingredients !== null && di.ingredients !== undefined)
    .map(di => {
      const ingredients = di.ingredients;
      const ing = Array.isArray(ingredients)
        ? (ingredients[0] as Record<string, unknown>)
        : (ingredients as Record<string, unknown>);

      if (!ing) return null;

      return {
        id: di.id as string,
        dish_id: di.dish_id as string,
        ingredient_id: di.ingredient_id as string | null,
        quantity: di.quantity as number,
        unit: di.unit as string,
        ingredients: {
          ...ing,
          supplier_name: (ing.supplier as string) || (ing.supplier_name as string) || 'Unknown',
        },
      } as DishRelationIngredient;
    })
    .filter((di): di is DishRelationIngredient => di !== null);

  return validDishIngredients;
}

import { ApiErrorHandler } from '@/lib/api-error-handler';
import { logger } from '@/lib/logger';
import { supabaseAdmin } from '@/lib/supabase';
import { DishRecipe } from '@/types/dish';
import { RecipeRecord } from '../../types';

/**
 * Fetches recipes for a dish
 *
 * @param {string} dishId - Dish ID
 * @returns {Promise<DishRecipe[]>} Valid dish recipes
 */
export async function fetchDishRecipes(dishId: string): Promise<DishRecipe[]> {
  if (!supabaseAdmin) {
    logger.error('[Dishes API] Database connection not available for fetchDishRecipes');
    throw ApiErrorHandler.createError('Database connection not available', 'DATABASE_ERROR', 500);
  }

  logger.dev('[fetchDishRecipes] Starting fetch for dishId:', { dishId });
  logger.dev('[fetchDishRecipes] Using supabaseAdmin:', { available: !!supabaseAdmin });

  // Fetch dish_recipes separately to avoid nested relation issues (same approach as audit endpoint)

  const { data: dishRecipesData, error: dishRecipesError } = await supabaseAdmin
    .from('dish_recipes')
    .select('recipe_id, quantity')
    .eq('dish_id', dishId);

  // Log error if present (but don't fail the whole request)

  if (dishRecipesError) {
    logger.warn('[Dishes API] Error fetching dish recipes (non-fatal):', {
      error: dishRecipesError.message,
      code: dishRecipesError.code,
      context: { endpoint: '/api/dishes/[id]', operation: 'GET', dishId },
    });
    return [];
  }

  if (!dishRecipesData || dishRecipesData.length === 0) {
    return [];
  }

  // Fetch recipe details separately
  const recipeIds = dishRecipesData.map(dr => dr.recipe_id).filter(Boolean);
  if (recipeIds.length === 0) {
    return [];
  }

  // Fetch recipe details - use both 'name' and 'recipe_name' column (database uses 'name', not 'recipe_name')

  const { data: recipesData, error: recipesError } = await supabaseAdmin
    .from('recipes')
    .select('id, name, recipe_name, description, yield, yield_unit, instructions')
    .in('id', recipeIds);

  if (recipesError) {
    logger.warn('[Dishes API] Error fetching recipe details (non-fatal):', {
      error: recipesError.message,
      code: recipesError.code,
      context: { endpoint: '/api/dishes/[id]', operation: 'GET', dishId },
    });
  }

  // Create a map of recipe_id -> recipe data
  const recipesMap = new Map<string, any>(
    (recipesData || []).map(r => {
      const recipeRecord = r as unknown as RecipeRecord;
      return [
        r.id,
        {
          id: r.id,
          name: recipeRecord.name || '',
          recipe_name: recipeRecord.recipe_name || '',
          description: r.description,
          yield: r.yield,
          yield_unit: r.yield_unit,
          instructions: r.instructions,
        },
      ];
    }),
  );

  // Combine dish_recipes with recipe data
  const validDishRecipes = dishRecipesData
    .filter(dr => {
      const hasRecipe = recipesMap.has(dr.recipe_id);
      if (!hasRecipe && dr.recipe_id) {
        logger.warn('[Dishes API] dish_recipes entry references non-existent recipe:', {
          dishId,
          recipeId: dr.recipe_id,
        });
      }
      return hasRecipe;
    })
    .map((dr, index) => {
      const recipeData = recipesMap.get(dr.recipe_id);
      return {
        id: `temp-${index}`,
        dish_id: dishId,
        recipe_id: dr.recipe_id,
        quantity: typeof dr.quantity === 'string' ? parseFloat(dr.quantity) : dr.quantity,
        recipes: recipeData,
      };
    });

  return validDishRecipes as unknown as DishRecipe[];
}

import { ApiErrorHandler } from '@/lib/api-error-handler';
import { logger } from '@/lib/logger';
import { PostgrestError } from '@supabase/supabase-js';
import { DishIngredientInput } from '../../../helpers/schemas'; // Adjusted path
import { updateIngredientsWithTracking } from '../updateDishRelations';

export async function handleIngredientUpdates(
  dishId: string,
  dishName: string,
  ingredients: DishIngredientInput[] | undefined,
  changes: string[],
  changeDetails: any, // justified
  userEmail: string,
): Promise<void> {
  if (!ingredients) return;

  try {
    await updateIngredientsWithTracking(
      dishId,
      dishName,
      ingredients,
      changes,
      changeDetails,
      userEmail,
    );
  } catch (ingredientsError) {
    if (
      ingredientsError &&
      typeof ingredientsError === 'object' &&
      'code' in ingredientsError &&
      'message' in ingredientsError
    ) {
      const pgError = ingredientsError as PostgrestError;
      logger.error('[Dishes API] Database error updating dish ingredients:', {
        error: pgError.message,
        code: pgError.code,
        context: { endpoint: '/api/dishes/[id]', operation: 'PUT', dishId },
      });
      throw ApiErrorHandler.fromSupabaseError(pgError, 500);
    }
    logger.error('[Dishes API] Unexpected error updating dish ingredients:', ingredientsError);
    throw ApiErrorHandler.createError('Failed to update ingredients', 'UPDATE_ERROR', 500);
  }
}

import { ApiErrorHandler } from '@/lib/api-error-handler';
import { logger } from '@/lib/logger';
import { PostgrestError } from '@supabase/supabase-js';
import { DishRecipeInput } from '../../../helpers/schemas'; // Adjusted path
import { invalidateAllergenCache, invalidateMenuPricingCache } from '../invalidateDishCaches';
import { updateRecipesWithTracking } from '../updateDishRelations';

export async function handleRecipeUpdates(
  dishId: string,
  dishName: string,
  recipes: DishRecipeInput[] | undefined,
  changes: string[],
  changeDetails: any, // justified
  userEmail: string,
): Promise<void> {
  if (!recipes) return;

  try {
    await updateRecipesWithTracking(dishId, recipes, changes, changeDetails);

    // Invalidate caches in background
    (async () => {
      try {
        await invalidateAllergenCache(dishId);
      } catch (err) {
        logger.error('[Dishes API] Error invalidating allergen cache:', {
          error: err instanceof Error ? err.message : String(err),
          context: { dishId, operation: 'invalidateAllergenCache' },
        });
      }
    })();

    (async () => {
      try {
        await invalidateMenuPricingCache(
          dishId,
          dishName,
          'recipes_changed',
          changeDetails.recipes as Record<string, unknown>,
          userEmail,
        );
      } catch (err) {
        logger.error('[Dishes API] Error invalidating menu pricing cache:', {
          error: err instanceof Error ? err.message : String(err),
          context: { dishId, dishName, operation: 'invalidateMenuPricingCache' },
        });
      }
    })();
  } catch (recipesError) {
    if (
      recipesError &&
      typeof recipesError === 'object' &&
      'code' in recipesError &&
      'message' in recipesError
    ) {
      const pgError = recipesError as PostgrestError;
      logger.error('[Dishes API] Database error updating dish recipes:', {
        error: pgError.message,
        code: pgError.code,
        context: { endpoint: '/api/dishes/[id]', operation: 'PUT', dishId },
      });
      throw ApiErrorHandler.fromSupabaseError(pgError, 500);
    }
    logger.error('[Dishes API] Unexpected error updating dish recipes:', recipesError);
    throw ApiErrorHandler.createError('Failed to update recipes', 'UPDATE_ERROR', 500);
  }
}

import { ApiErrorHandler } from '@/lib/api-error-handler';
import { logger } from '@/lib/logger';
import { supabaseAdmin } from '@/lib/supabase';

export async function performDishUpdate(dishId: string, updateData: any) { // justified
  if (!supabaseAdmin) {
    throw new Error('Database connection not available');
  }

  const { data: updatedDish, error: updateError } = await supabaseAdmin
    .from('dishes')
    .update(updateData)
    .eq('id', dishId)
    .select()
    .single();

  if (updateError) {
    logger.error('[Dishes API] Database error updating dish:', {
      error: updateError.message,
      code: updateError.code,
      context: { endpoint: '/api/dishes/[id]', operation: 'PUT', dishId },
    });

    const apiError = ApiErrorHandler.fromSupabaseError(updateError, 500);
    throw apiError; // Throwing to be caught by main handler or returned as response if I change return type
  }

  return updatedDish;
}

import { logger } from '@/lib/logger';
import { triggerCostSync, triggerDishSync } from '@/lib/square/sync/hooks';
import { NextRequest } from 'next/server';
import { trackChangeForLockedMenus } from '../invalidateDishCaches';

export async function triggerSideEffects(
  request: NextRequest,
  dishId: string,
  dishName: string,
  changes: string[],
  changeDetails: any, // justified
  userEmail: string,
) {
  // Track price changes for locked menus
  if (changes.includes('price_changed')) {
    (async () => {
      try {
        await trackChangeForLockedMenus(
          dishId,
          dishName,
          'price_changed',
          changeDetails.price as Record<string, unknown>,
          userEmail,
        );
      } catch (err) {
        logger.error('[Dishes API] Error tracking price change:', {
          error: err instanceof Error ? err.message : String(err),
          context: { dishId, dishName, operation: 'trackChangeForLockedMenus' },
        });
      }
    })();
  }

  // Trigger Square sync hooks (non-blocking)
  // Always trigger dish sync for updates
  (async () => {
    try {
      await triggerDishSync(request, dishId, 'update');
    } catch (err) {
      logger.error('[Dishes API] Error triggering Square dish sync:', {
        error: err instanceof Error ? err.message : String(err),
        dishId,
      });
    }
  })();

  // Trigger cost sync if recipes or ingredients changed
  if (changes.includes('recipes_changed') || changes.includes('ingredients_changed')) {
    (async () => {
      try {
        await triggerCostSync(request, dishId, 'dish_updated');
      } catch (err) {
        logger.error('[Dishes API] Error triggering Square cost sync:', {
          error: err instanceof Error ? err.message : String(err),
          dishId,
        });
      }
    })();
  }
}

/**
 * Helper to calculate cost for a single dish.
 */

import { calculateRecommendedPrice } from '@/app/api/dishes/helpers/calculateRecommendedPrice';
import { calculateRecipeCost } from '@/app/api/menus/[id]/statistics/helpers/calculateRecipeCost';
import { logger } from '@/lib/logger';
import { DishIngredientRecord, DishRecipeRecord } from '../../../types';

export interface BatchCostResult {
  total_cost: number;
  selling_price: number;
  gross_profit: number;
  gross_profit_margin: number;
  food_cost_percent: number;
  contributingMargin: number;
  contributingMarginPercent: number;
  recommendedPrice: number;
}

/**
 * Calculate cost and recommended price for a single dish.
 */
// Basic interfaces for batch calculation
export interface BatchDishInput {
  selling_price: string | number | null;
}

export interface BatchDishRecipeInput {
  recipe_id: string;
  quantity: string | number | null;
}

export interface BatchIngredient {
  cost_per_unit_incl_trim?: number;
  cost_per_unit?: number;
  category?: string;
  trim_peel_waste_percentage?: number;
  yield_percentage?: number;
}

export interface BatchDishIngredientInput {
  ingredients: BatchIngredient | null;
  quantity: string | number | null;
}

export interface DishCostResult {
  total_cost: number;
  selling_price: number;
  gross_profit: number;
  gross_profit_margin: number;
  food_cost_percent: number;
  contributingMargin: number;
  contributingMarginPercent: number;
  recommendedPrice: number | null; // calculateRecommendedPrice can return null based on its impl, assuming number | null here
}

/**
 * Calculate cost and recommended price for a single dish.
 */
export async function calculateDishCost(
  dishId: string,
  dish: { selling_price: number | string | null } | null,
  dishRecipes: DishRecipeRecord[],
  dishIngredients: DishIngredientRecord[],
): Promise<{ dishId: string; cost: BatchCostResult } | { dishId: string; cost: null }> {
  if (!dish) {
    logger.warn(`[Dishes API] Dish ${dishId} not found in batch`);
    return { dishId, cost: null };
  }

  let totalCost = 0;

  // Calculate cost from recipes
  for (const dishRecipe of dishRecipes) {
    try {
      const recipeQuantity =
        typeof dishRecipe.quantity === 'string'
          ? parseFloat(dishRecipe.quantity)
          : dishRecipe.quantity || 1;

      const recipeCost = await calculateRecipeCost(dishRecipe.recipe_id, recipeQuantity);
      totalCost += recipeCost;
    } catch (err) {
      logger.error('[Dishes API] Error calculating recipe cost in batch:', {
        dishId,
        recipeId: dishRecipe.recipe_id,
        error: err instanceof Error ? err.message : String(err),
      });
      // Continue with other recipes instead of failing completely
    }
  }

  // Calculate cost from standalone ingredients
  for (const di of dishIngredients) {
    const ingredient = di.ingredients as Record<string, any> | undefined;
    if (ingredient) {
      const costPerUnit =
        (ingredient.cost_per_unit_incl_trim as number) || (ingredient.cost_per_unit as number) || 0;
      const quantity = typeof di.quantity === 'string' ? parseFloat(di.quantity) : di.quantity || 0;

      const isConsumable = ingredient.category === 'Consumables';

      // For consumables: simple calculation (no waste/yield)
      if (isConsumable) {
        totalCost += quantity * costPerUnit;
        continue;
      }

      // For regular ingredients: apply waste/yield adjustments
      const wastePercent = ingredient.trim_peel_waste_percentage || 0;
      const yieldPercent = ingredient.yield_percentage || 100;

      let adjustedCost = quantity * costPerUnit;
      if (!ingredient.cost_per_unit_incl_trim && wastePercent > 0) {
        adjustedCost = adjustedCost / (1 - wastePercent / 100);
      }
      adjustedCost = adjustedCost / (yieldPercent / 100);

      totalCost += adjustedCost;
    }
  }

  const sellingPrice = (dish.selling_price as number) || 0;

  const grossProfit = sellingPrice - totalCost;
  const grossProfitMargin = sellingPrice > 0 ? (grossProfit / sellingPrice) * 100 : 0;
  const foodCostPercent = sellingPrice > 0 ? (totalCost / sellingPrice) * 100 : 0;

  // Calculate contributing margin (Revenue excl GST - Food Cost)
  const gstRate = 0.1; // 10% GST for Australia
  const sellingPriceExclGST = sellingPrice / (1 + gstRate);
  const contributingMargin = sellingPriceExclGST - totalCost;
  const contributingMarginPercent =
    sellingPriceExclGST > 0 ? (contributingMargin / sellingPriceExclGST) * 100 : 0;

  // Calculate recommended price using same formula as recipes
  const recommendedPrice = calculateRecommendedPrice(totalCost);

  return {
    dishId,
    cost: {
      total_cost: totalCost,
      selling_price: sellingPrice,
      gross_profit: grossProfit,
      gross_profit_margin: grossProfitMargin,
      food_cost_percent: foodCostPercent,
      contributingMargin: contributingMargin,
      contributingMarginPercent: contributingMarginPercent,
      recommendedPrice: recommendedPrice,
    },
  };
}

import { ApiErrorHandler } from '@/lib/api-error-handler';
import {
  enableGoogleConnectionForApp,
  verifyGoogleConnection,
} from '@/lib/auth0-google-connection';
import { getSocialConnections, verifyCallbackUrls } from '@/lib/auth0-management';
import { logger } from '@/lib/logger';
import { ManagementClient } from 'auth0';
import { NextResponse } from 'next/server';
import { buildRequiredUrls } from './helpers/buildRequiredUrls';

/**
 * Fix Auth0 Callback URLs via Management API
 * Automatically adds missing callback URLs, logout URLs, and web origins
 */
export async function POST() {
  try {
    const auth0Issuer = process.env.AUTH0_ISSUER_BASE_URL;
    // Try M2M credentials first (more secure), fall back to regular app credentials
    const m2mClientId = process.env.AUTH0_M2M_CLIENT_ID;
    const m2mClientSecret = process.env.AUTH0_M2M_CLIENT_SECRET;
    const auth0ClientId = process.env.AUTH0_CLIENT_ID;
    const auth0ClientSecret = process.env.AUTH0_CLIENT_SECRET;
    // Use AUTH0_BASE_URL (Auth0 SDK)
    const baseUrl = process.env.AUTH0_BASE_URL;

    if (!auth0Issuer) {
      return NextResponse.json(
        ApiErrorHandler.createError('AUTH0_ISSUER_BASE_URL must be set', 'MISSING_ENV_VAR', 400),
        { status: 400 },
      );
    }

    if (!baseUrl) {
      return NextResponse.json(
        ApiErrorHandler.createError('AUTH0_BASE_URL must be set', 'MISSING_ENV_VAR', 400),
        { status: 400 },
      );
    }

    // Determine which credentials to use (M2M preferred, fall back to regular app)
    const useM2M = m2mClientId && m2mClientSecret;
    const managementClientId = useM2M ? m2mClientId : auth0ClientId;
    const managementClientSecret = useM2M ? m2mClientSecret : auth0ClientSecret;

    if (!managementClientId || !managementClientSecret) {
      return NextResponse.json(
        ApiErrorHandler.createError(
          'Either AUTH0_M2M_CLIENT_ID/AUTH0_M2M_CLIENT_SECRET (recommended) or AUTH0_CLIENT_ID/AUTH0_CLIENT_SECRET must be set',
          'MISSING_AUTH0_CREDENTIALS',
          400,
          {
            hint: 'See docs/AUTH0_MANAGEMENT_API_SETUP.md for setup instructions',
          },
        ),
        { status: 400 },
      );
    }

    // The application client ID to update (always use the main app, not M2M)
    const applicationClientId = auth0ClientId;
    if (!applicationClientId) {
      return NextResponse.json(
        ApiErrorHandler.createError(
          'AUTH0_CLIENT_ID must be set to identify which application to update',
          'MISSING_ENV_VAR',
          400,
        ),
        { status: 400 },
      );
    }

    const domain = auth0Issuer.replace(/^https?:\/\//, '').replace(/\/$/, '');
    const managementClient = new ManagementClient({
      domain,
      clientId: managementClientId,
      clientSecret: managementClientSecret,
    });

    interface Auth0ClientData {
      callbacks?: string[];
      allowed_logout_urls?: string[];
      logout_urls?: string[];
      web_origins?: string[];
    }

    // Get current application configuration
    const appResponse = await managementClient.clients.get({ client_id: applicationClientId });
    // Handle both potential response structures (axios-like with data or direct)
    const app = ('data' in appResponse
      ? appResponse.data
      : appResponse) as unknown as Auth0ClientData;

    // Build required URLs (Auth0 SDK uses /api/auth/callback, not /api/auth/callback/auth0)
    const { requiredCallbacks, requiredLogoutUrls, requiredWebOrigins } =
      buildRequiredUrls(baseUrl);

    // Merge with existing URLs (avoid duplicates)
    const currentCallbacks = app.callbacks || [];
    // Auth0 Management API uses 'allowed_logout_urls' (with underscores, not 'logout_urls')
    const currentLogoutUrls = app.allowed_logout_urls || app.logout_urls || [];
    const currentWebOrigins = app.web_origins || [];

    const updatedCallbacks = [...new Set([...currentCallbacks, ...requiredCallbacks])];
    const updatedLogoutUrls = [...new Set([...currentLogoutUrls, ...requiredLogoutUrls])];
    const updatedWebOrigins = [...new Set([...currentWebOrigins, ...requiredWebOrigins])];

    // Update application
    // Auth0 Management API expects 'allowed_logout_urls' (not 'logout_urls')
    const updateData: Record<string, unknown> = {
      callbacks: updatedCallbacks,
      allowed_logout_urls: updatedLogoutUrls,
      web_origins: updatedWebOrigins,
    };

    await managementClient.clients.update(
      { client_id: applicationClientId },
      updateData as unknown as any, // Cast required due to strict SDK types not matching exact update payload sometimes // justified
    );

    logger.info('[Auth0 Fix] Updated Auth0 application configuration', {
      usingM2M: useM2M,
      callbacks: updatedCallbacks,
      logoutUrls: updatedLogoutUrls,
      webOrigins: updatedWebOrigins,
    });

    // Verify social connections
    const socialConnections = await getSocialConnections();
    let googleConnectionVerified = await verifyGoogleConnection();

    // Try to enable Google connection if it exists but isn't enabled
    if (!googleConnectionVerified) {
      const enableResult = await enableGoogleConnectionForApp();
      if (enableResult.success && enableResult.enabled) {
        googleConnectionVerified = true;
        logger.info('[Auth0 Fix] Auto-enabled Google connection for application');
      }
    }

    const callbackUrlStatus = await verifyCallbackUrls(updatedCallbacks);

    // Build response with social connection status
    const response = {
      success: true,
      message: 'Auth0 configuration updated successfully',
      changes: {
        callbacks: {
          before: currentCallbacks,
          after: updatedCallbacks,
          added: requiredCallbacks.filter(url => !currentCallbacks.includes(url)),
        },
        logoutUrls: {
          before: currentLogoutUrls,
          after: updatedLogoutUrls,
          added: requiredLogoutUrls.filter(url => !currentLogoutUrls.includes(url)),
        },
        webOrigins: {
          before: currentWebOrigins,
          after: updatedWebOrigins,
          added: requiredWebOrigins.filter(url => !currentWebOrigins.includes(url)),
        },
      },
      socialConnections: {
        total: socialConnections.length,
        connections: socialConnections.map(conn => ({
          id: conn.id,
          name: conn.name,
          strategy: conn.strategy,
          enabled: conn.enabled_clients?.includes(applicationClientId) || false,
        })),
        google: {
          verified: googleConnectionVerified,
          status: googleConnectionVerified ? 'enabled' : 'disabled_or_misconfigured',
          message: googleConnectionVerified
            ? 'Google connection is enabled and configured correctly'
            : 'Google connection is not enabled or misconfigured. Please enable it in Auth0 Dashboard > Connections > Social > Google',
        },
      },
      callbackUrlVerification: {
        verified: callbackUrlStatus.isValid,
        configured: callbackUrlStatus.configuredUrls,
        expected: callbackUrlStatus.expectedUrls,
        missing: callbackUrlStatus.missingUrls,
      },
    };

    return NextResponse.json(response);
  } catch (error) {
    logger.error('[Auth0 Fix] Error updating Auth0 configuration:', error);
    return NextResponse.json(
      ApiErrorHandler.createError('Failed to update Auth0 configuration', 'SERVER_ERROR', 500, {
        details: error instanceof Error ? error.message : String(error),
      }),
      { status: 500 },
    );
  }
}

import { enrichIngredientWithAllergensHybrid } from '@/lib/allergens/hybrid-allergen-detection';
import { logger } from '@/lib/logger';
import { supabaseAdmin } from '@/lib/supabase';
import { UpdateIngredientData } from '../schemas';

async function performHybridDetection(
  id: string,
  ingredientName: string,
  ingredientBrand: string | null,
  currentIngredient: any /* justified: complex DB record */,
  formattedUpdates: any /* justified: dynamic update payload */,
) {
  try {
    const enriched = await enrichIngredientWithAllergensHybrid({
      ingredient_name: ingredientName,
      brand: ingredientBrand || undefined,
      allergens: (currentIngredient.allergens as string[]) || [],
      allergen_source:
        (currentIngredient.allergen_source as {
          manual?: boolean;
          ai?: boolean;
        }) || {},
    });

    formattedUpdates.allergens = enriched.allergens;
    formattedUpdates.allergen_source = enriched.allergen_source;

    if (enriched.allergens.length > 0) {
      logger.dev('[Ingredients API] Auto-detected allergens on update:', {
        ingredient_id: id,
        ingredient_name: ingredientName,
        allergens: enriched.allergens,
        method: enriched.method,
      });
    }
    return true;
  } catch (err) {
    logger.warn('[Ingredients API] Failed to auto-detect allergens on update:', {
      error: err instanceof Error ? err.message : String(err),
      stack: err instanceof Error ? err.stack : undefined,
    });
    return false;
  }
}

export async function detectCurrentAllergens(
  id: string,
  updates: UpdateIngredientData,
  formattedUpdates: any /* justified: dynamic update payload */,
) {
  const allergensChanged = updates.allergens !== undefined;
  const nameOrBrandChanged = updates.ingredient_name !== undefined || updates.brand !== undefined;

  if (allergensChanged || !nameOrBrandChanged) {
    return allergensChanged;
  }

  if (!supabaseAdmin) {
    logger.error('[Ingredients API] Supabase admin not available for allergen check');
    return allergensChanged;
  }

  // 1. Fetch current data
  const { data: currentIngredient, error: fetchError } = await supabaseAdmin
    .from('ingredients')
    .select('ingredient_name, brand, allergens, allergen_source')
    .eq('id', id)
    .maybeSingle();

  if (fetchError || !currentIngredient) {
    if (fetchError && fetchError.code !== 'PGRST116') {
      logger.warn('[Ingredients API] Error fetching current ingredient for allergen check:', {
        error: fetchError.message,
        code: fetchError.code,
        ingredientId: id,
      });
    }
    return allergensChanged;
  }

  // 2. Check for manual allergens
  const hasManualAllergens =
    currentIngredient.allergen_source &&
    typeof currentIngredient.allergen_source === 'object' &&
    (currentIngredient.allergen_source as { manual?: boolean }).manual;

  if (hasManualAllergens) {
    return allergensChanged;
  }

  // 3. Perform detection
  const ingredientName = formattedUpdates.ingredient_name || currentIngredient.ingredient_name;
  const ingredientBrand =
    formattedUpdates.brand !== undefined ? formattedUpdates.brand : currentIngredient.brand;

  if (!ingredientName) {
    return allergensChanged;
  }

  const detected = await performHybridDetection(
    id,
    ingredientName,
    ingredientBrand,
    currentIngredient,
    formattedUpdates,
  );
  return detected || allergensChanged;
}

