}

/**
 * Ingredients API Routes
 *
 *  Square Integration: This route automatically triggers Square sync hooks after ingredient
 * create/update operations. See `docs/SQUARE_API_REFERENCE.md` (Automatic Sync section) for details.
 */

import { ApiErrorHandler } from '@/lib/api-error-handler';
import { getUserEmail } from '@/lib/auth0-api-helpers';
import { logger } from '@/lib/logger';
import { triggerIngredientSync } from '@/lib/square/sync/hooks';
import { createSupabaseAdmin } from '@/lib/supabase';
import { NextRequest, NextResponse } from 'next/server';
import { createIngredient } from './helpers/createIngredient';
import { handleDeleteIngredient } from './helpers/deleteIngredientHandler';
import { handleIngredientError } from './helpers/handleIngredientError';
import { createIngredientSchema, updateIngredientSchema } from './helpers/schemas';
import { updateIngredient } from './helpers/updateIngredient';

// Helper to safely parse request body
async function safeParseBody(request: NextRequest) {
  try {
    return await request.json();
  } catch (err) {
    logger.warn('[Ingredients API] Failed to parse request JSON:', {
      error: err instanceof Error ? err.message : String(err),
    });
    return null;
  }
}

export async function GET(request: NextRequest) {
  try {
    const supabaseAdmin = createSupabaseAdmin();

    const { searchParams } = new URL(request.url);
    const page = parseInt(searchParams.get('page') || '1');
    const pageSize = parseInt(searchParams.get('pageSize') || '20');

    const start = (page - 1) * pageSize;
    const end = start + pageSize - 1;

    const { data, error, count } = await supabaseAdmin
      .from('ingredients')
      .select('*', { count: 'exact' })
      .order('ingredient_name')
      .range(start, end);

    if (error) {
      logger.error('[Ingredients API] Database error fetching ingredients:', {
        error: error.message,
        code: error.code,
        context: { endpoint: '/api/ingredients', operation: 'GET', table: 'ingredients' },
      });

      const apiError = ApiErrorHandler.fromSupabaseError(error, 500);
      return NextResponse.json(apiError, { status: apiError.status || 500 });
    }

    return NextResponse.json({
      success: true,
      data: {
        items: data || [],
        total: count || 0,
        page,
        pageSize,
        totalPages: Math.ceil((count || 0) / pageSize),
      },
    });
  } catch (err) {
    logger.error('[Ingredients API] Unexpected error:', {
      error: err instanceof Error ? err.message : String(err),
      context: { endpoint: '/api/ingredients', method: 'GET' },
    });
    return handleIngredientError(err, 'GET');
  }
}

export async function POST(request: NextRequest) {
  try {
    let body: unknown;
    try {
      body = await request.json();
    } catch (err) {
      logger.warn('[Ingredients API] Failed to parse request body:', {
        error: err instanceof Error ? err.message : String(err),
      });
      return NextResponse.json(
        ApiErrorHandler.createError('Invalid request body', 'VALIDATION_ERROR', 400),
        { status: 400 },
      );
    }

    const validationResult = createIngredientSchema.safeParse(body);
    if (!validationResult.success) {
      return NextResponse.json(
        ApiErrorHandler.createError(
          validationResult.error.issues[0]?.message || 'Invalid request body',
          'VALIDATION_ERROR',
          400,
        ),
        { status: 400 },
      );
    }

    const data = await createIngredient(validationResult.data);

    // Trigger Square sync hook (non-blocking)
    (async () => {
      try {
        await triggerIngredientSync(request, data.id, 'create');
      } catch (err) {
        logger.error('[Ingredients API] Error triggering Square sync:', {
          error: err instanceof Error ? err.message : String(err),
          ingredientId: data.id,
        });
      }
    })();

    return NextResponse.json({
      success: true,
      data,
    });
  } catch (err) {
    logger.error('[Ingredients API] Unexpected error:', {
      error: err instanceof Error ? err.message : String(err),
      context: { endpoint: '/api/ingredients', method: 'POST' },
    });
    if (err && typeof err === 'object' && 'status' in err && typeof err.status === 'number') {
      return NextResponse.json(err, { status: (err as { status: number }).status });
    }
    return handleIngredientError(err, 'POST');
  }
}

export async function PUT(request: NextRequest) {
  try {
    let body: unknown;
    try {
      body = await request.json();
    } catch (err) {
      logger.warn('[Ingredients API] Failed to parse request body:', {
        error: err instanceof Error ? err.message : String(err),
      });
      return NextResponse.json(
        ApiErrorHandler.createError('Invalid request body', 'VALIDATION_ERROR', 400),
        { status: 400 },
      );
    }

    const validationResult = updateIngredientSchema.safeParse(body);
    if (!validationResult.success) {
      return NextResponse.json(
        ApiErrorHandler.createError(
          validationResult.error.issues[0]?.message || 'Invalid request body',
          'VALIDATION_ERROR',
          400,
        ),
        { status: 400 },
      );
    }

    const { id, ...updates } = validationResult.data;

    // Get user email for change tracking
    let userEmail: string | null = null;
    try {
      userEmail = await getUserEmail(request);
    } catch (authError) {
      // Continue without user email if auth fails (for development)
      logger.warn('[Ingredients API] Could not get user email for change tracking:', authError);
    }

    const data = await updateIngredient(id, updates, userEmail);

    // Trigger Square sync hook (non-blocking)
    (async () => {
      try {
        await triggerIngredientSync(request, id, 'update');
      } catch (err) {
        logger.error('[Ingredients API] Error triggering Square sync:', {
          error: err instanceof Error ? err.message : String(err),
          ingredientId: id,
        });
      }
    })();

    return NextResponse.json({
      success: true,
      data,
    });
  } catch (err) {
    logger.error('[Ingredients API] Unexpected error:', {
      error: err instanceof Error ? err.message : String(err),
      context: { endpoint: '/api/ingredients', method: 'PUT' },
    });
    if (err && typeof err === 'object' && 'status' in err && typeof err.status === 'number') {
      return NextResponse.json(err, { status: (err as { status: number }).status });
    }
    return handleIngredientError(err, 'PUT');
  }
}

export async function DELETE(request: NextRequest) {
  return handleDeleteIngredient(request);
}

import { logger } from '@/lib/logger';
import type { SupabaseClient } from '@supabase/supabase-js';
import { generateDataHash } from '../../cardBuilding';
import { MenuItemData } from '../../fetchMenuItemData';
import { MenuItem } from '../fetchMenuItems';
import { ItemToGenerate } from '../types';

export interface OldMethodCheckResult {
  reused: boolean;
  itemToGenerate?: ItemToGenerate;
}

export async function checkByOldMethod(
  supabase: SupabaseClient,
  menuItem: MenuItem,
  menuItemData: MenuItemData,
  normalizedIngredients: any[], // justified
  signature: string,
): Promise<OldMethodCheckResult> {
  const currentHash = generateDataHash(menuItemData, normalizedIngredients);
  logger.dev(
    `[Card Reuse] Checking old method for menu item ${menuItem.id} (hash: ${currentHash.substring(0, 8)}...)`,
  );

  const { data: existingCardOld, error: cardError } = await supabase
    .from('menu_recipe_cards')
    .select('id, data_hash')
    .eq('menu_item_id', menuItem.id)
    .single();

  if (!cardError && existingCardOld) {
    logger.dev(
      `[Card Reuse] Found existing card ${existingCardOld.id} for menu_item_id ${menuItem.id}`,
    );
    logger.dev(
      `[Card Reuse] Comparing hashes - existing: ${existingCardOld.data_hash?.substring(0, 8) || 'none'}..., current: ${currentHash.substring(0, 8)}...`,
    );

    if (existingCardOld.data_hash === currentHash) {
      logger.dev(
        `[Card Reuse]  Reusing existing card ${existingCardOld.id} for menu item ${menuItem.id} (hash matches)`,
      );
      return { reused: true };
    } else {
      logger.dev(
        `[Card Reuse]  Card exists for menu item ${menuItem.id} but hash changed (old: ${existingCardOld.data_hash?.substring(0, 8) || 'none'}..., new: ${currentHash.substring(0, 8)}...), will regenerate`,
      );
      return {
        reused: false,
        itemToGenerate: { menuItem, menuItemData, signature },
      };
    }
  } else {
    if (cardError && cardError.code === 'PGRST116') {
      logger.dev(
        `[Card Reuse]  No existing card found for menu item ${menuItem.id} (PGRST116 - no rows), will generate new card`,
      );
    } else if (cardError) {
      logger.warn(
        `[Card Reuse] Error checking for existing card for menu item ${menuItem.id}:`,
        cardError,
      );
    } else {
      logger.dev(
        `[Card Reuse]  No existing card found for menu item ${menuItem.id}, will generate new card`,
      );
    }
    return {
      reused: false,
      itemToGenerate: { menuItem, menuItemData, signature },
    };
  }
}

import { logger } from '@/lib/logger';
import type { SupabaseClient } from '@supabase/supabase-js';
import { generateDataHash } from '../../cardBuilding';
import { findExistingCardBySignature, getRecipeSignature } from '../../cardManagement';
import { MenuItemData } from '../../fetchMenuItemData';
import { MenuItem } from '../fetchMenuItems';
import { ItemToGenerate } from '../types';

// Define result type
export interface SignatureCheckResult {
  handled: boolean;
  action?: 'link' | 'generate';
  cardId?: string;
  signature?: string;
  itemToGenerate?: ItemToGenerate;
}

export async function checkBySignature(
  supabase: SupabaseClient,
  menuItem: MenuItem,
  menuItemData: MenuItemData,
  normalizedIngredients: any[], // justified
): Promise<SignatureCheckResult> {
  const signature = getRecipeSignature(menuItemData, menuItem) || '';
  if (!signature) {
    logger.dev(
      `[Card Reuse] Could not determine signature for menu item ${menuItem.id}, falling back to old method`,
    );
    return { handled: false };
  }

  logger.dev(
    `[Card Reuse] Menu item ${menuItem.id} signature: ${signature} (dish_id: ${menuItem.dish_id}, recipe_id: ${menuItem.recipe_id})`,
  );

  const existingCard = await findExistingCardBySignature(supabase, signature, menuItem);
  if (!existingCard) {
    logger.dev(
      `[Card Reuse]  No existing card found for signature ${signature} (dish_id: ${menuItem.dish_id}, recipe_id: ${menuItem.recipe_id}), falling back to old method check`,
    );
    return { handled: false, signature };
  }

  logger.dev(`[Card Reuse]  Found existing card ${existingCard.id} for signature ${signature}`);
  const currentHash = generateDataHash(menuItemData, normalizedIngredients);
  logger.dev(
    `[Card Reuse] Comparing hashes - existing: ${existingCard.data_hash?.substring(0, 8) || 'none'}..., current: ${currentHash.substring(0, 8)}...`,
  );

  if (existingCard.data_hash === currentHash) {
    logger.dev(
      `[Card Reuse]  Linking menu item ${menuItem.id} to existing card ${existingCard.id} (signature: ${signature}, hash matches)`,
    );
    return { handled: true, action: 'link', cardId: existingCard.id, signature };
  } else {
    logger.dev(
      `[Card Reuse]  Card exists but data changed for menu item ${menuItem.id} (hash mismatch), will regenerate and update existing card...`,
    );
    return {
      handled: true,
      action: 'generate',
      signature,
      itemToGenerate: {
        menuItem,
        menuItemData,
        signature,
        existingCardId: existingCard.id,
      },
    };
  }
}

import { ApiErrorHandler } from '@/lib/api-error-handler';
import { logger } from '@/lib/logger';
import type { SupabaseClient } from '@supabase/supabase-js';
import { NextResponse } from 'next/server';
import { IngredientRecord, ParLevelRecord } from '../types';

export async function fetchFallback(supabaseAdmin: SupabaseClient, originalError: any /* justified: raw database error */) {
  logger.warn('[Par Levels API] Join failed, trying without join:', {
    error: originalError.message,
    code: originalError.code,
  });

  // Try simple select first
  const { data: simpleData, error: simpleError } = await supabaseAdmin
    .from('par_levels')
    .select('*')
    .order('created_at', { ascending: false });

  if (simpleError) {
    // Table or columns don't exist
    return {
      data: null,
      error: NextResponse.json(
        ApiErrorHandler.createError(
          `Database error: ${simpleError.message}. Please ensure the par_levels table exists and has the required columns.`,
          'DATABASE_ERROR',
          500,
          {
            error: simpleError.message,
            code: simpleError.code,
            instructions: [
              'Please run the migration script: migrations/add-par-levels-columns.sql',
              'Ensure the par_levels table exists with columns: id, ingredient_id, par_level, reorder_point, unit, created_at, updated_at',
            ],
          },
        ),
        { status: 500 },
      ),
    };
  }

  let data: any = simpleData; // justified: raw DB data
  let error: any = null; // justified: raw DB error

  // If we got data without join, fetch ingredients separately
  if (simpleData && simpleData.length > 0) {
    const ingredientIds = simpleData
      .map((pl: ParLevelRecord) => pl.ingredient_id)
      .filter((id: string | undefined) => id);

    if (ingredientIds.length > 0) {
      const { data: ingredientsData, error: ingredientsError } = await supabaseAdmin
        .from('ingredients')
        .select('id, ingredient_name, unit, category')
        .in('id', ingredientIds);

      if (ingredientsError) {
        logger.warn('[Par Levels API] Error fetching ingredients for par levels:', {
          error: ingredientsError.message,
          code: ingredientsError.code,
          ingredientIds,
        });
        // Continue without ingredients - par levels will have null ingredients
        data = simpleData.map((pl: ParLevelRecord) => ({ ...pl, ingredients: null }));
        error = null;
      } else {
        // Merge ingredients into par levels
        const ingredientsArray = (ingredientsData || []) as IngredientRecord[];
        const ingredientsMap = new Map(ingredientsArray.map(ing => [ing.id, ing]));
        data = simpleData.map((pl: ParLevelRecord) => ({
          ...pl,
          ingredients: ingredientsMap.get(pl.ingredient_id) || null,
        }));
        error = null;
      }
    } else {
      data = simpleData.map((pl: ParLevelRecord) => ({ ...pl, ingredients: null }));
      error = null;
    }
  }

  return { data: data || [], error };
}

/**
 * Action handlers for recipe processing
 */

import { isGroqAvailable, isGroqEnabled } from '@/lib/ai/groq-client';
import { logger } from '@/lib/logger';

// Processor module exports functions directly (not as an object)
type Processor = any;

/**
 * Handle pause action
 */
export function handlePauseAction(processor: Processor) {
  processor.pauseProcessing();
  const status = processor.getProcessingStatus();
  return {
    success: true,
    message: 'Processing paused',
    data: status,
  };
}

/**
 * Handle resume action
 */
export function handleResumeAction(processor: Processor) {
  processor.resumeProcessing();
  const status = processor.getProcessingStatus();
  return {
    success: true,
    message: 'Processing resumed',
    data: status,
  };
}

/**
 * Handle stop action
 */
export function handleStopAction(processor: Processor) {
  processor.stopProcessing();
  const status = processor.getProcessingStatus();
  return {
    success: true,
    message: 'Processing stopped',
    data: status,
  };
}

/**
 * Handle start action - starts background processing
 */
export async function handleStartAction(
  processor: Processor,
  limit?: number,
  model?: string,
): Promise<{ success: boolean; message: string; data: unknown; status?: number }> {
  // Check if already processing
  const currentStatus = processor.getProcessingStatus();
  if (currentStatus.isProcessing && !currentStatus.isPaused) {
    return {
      success: false,
      message: 'Processing is already in progress',
      data: currentStatus,
    };
  }

  // Check provider availability (Groq primary, Ollama fallback)
  const groqEnabled = isGroqEnabled();
  const groqAvailable = groqEnabled ? await isGroqAvailable() : false;

  // Select model based on provider
  const selectedModel = model || (groqAvailable ? 'llama-3.1-8b-instant' : 'llama3.2:3b');
  const provider = groqAvailable ? 'Groq API (fast, sub-second)' : 'Ollama (free, local)';

  // Respond immediately - processing will start in background
  const requestId = `req_${Date.now()}_${Math.random().toString(36).substring(7)}`;
  logger.info('[Process Recipes API] Processing request received, responding immediately', {
    requestId,
    limit,
    provider,
    model: selectedModel,
    groqEnabled,
    groqAvailable,
  });

  // Start background processing
  setImmediate(async () => {
    try {
      logger.info('[Process Recipes API] Starting background processing', {
        requestId,
        limit,
        model: selectedModel,
        provider,
        groqEnabled,
        groqAvailable,
      });

      await processor.processRecipes({
        limit,
        model: selectedModel,
      });

      logger.info('[Process Recipes API] Background processing completed', { requestId });
    } catch (err) {
      logger.error('[Process Recipes API] Background processing failed:', {
        requestId,
        error: err instanceof Error ? err.message : String(err),
        stack: err instanceof Error ? err.stack : undefined,
      });
    }
  });

  return {
    success: true,
    message: `Processing started in background (using ${provider})`,
    data: {
      provider,
      model: selectedModel,
      groqEnabled,
      groqAvailable,
      note: groqAvailable
        ? 'Using Groq API for fast processing (sub-second responses). Monitor usage at https://console.groq.com/'
        : groqEnabled
          ? 'Groq enabled but not available. Using Ollama fallback (free, local). Make sure Ollama is running: "ollama serve"'
          : 'Using Ollama (free, local). Enable Groq by setting USE_GROQ=true and GROQ_API_KEY=your-key for faster processing. See: docs/GROQ_SETUP_GUIDE.md',
    },
    status: 202, // 202 Accepted for background processing
  };
}

/**
 * Status handling helpers for recipe processing
 */

import { logger } from '@/lib/logger';
import { isGroqEnabled, isGroqAvailable } from '@/lib/ai/groq-client';

// Use any for status since it comes from the processor module
type ProcessingStatus = any;

/**
 * Get default status when processor fails
 */
export function getDefaultStatus(): ProcessingStatus {
  return {
    isProcessing: false,
    isPaused: false,
    queueLength: 0,
    activeProcessing: 0,
    totalProcessed: 0,
    totalRecipes: 0,
    progressPercent: 0,
    isStuck: false,
    healthStatus: 'healthy',
    aiProvider: 'Unknown',
    aiProviderModel: 'Unknown',
  };
}

/**
 * Enhance status with provider information and unformatted counts
 */
export async function enhanceProcessingStatus(
  status: ProcessingStatus,
  processor: {
    getProcessingStatus: () => any;
    countUnformattedRecipes: () => Promise<{ unformatted: number; formatted: number }>;
  },
): Promise<ProcessingStatus> {
  // If not processing, calculate unformatted recipe count to show accurate numbers
  if (!status.isProcessing) {
    try {
      const counts = await processor.countUnformattedRecipes();
      status.totalRecipes = counts.unformatted;
      status.skippedFormatted = counts.formatted;
      logger.dev('[Process Recipes API] Calculated unformatted recipe counts', counts);
    } catch (countErr) {
      logger.warn('[Process Recipes API] Failed to count unformatted recipes:', {
        error: countErr instanceof Error ? countErr.message : String(countErr),
      });
      // Continue with status.totalRecipes = 0 if counting fails
    }
  }

  // Set default provider if not set (Groq primary, Ollama fallback)
  if (!status.aiProvider) {
    const groqEnabled = isGroqEnabled();
    const groqAvailable = groqEnabled ? await isGroqAvailable() : false;

    if (groqAvailable) {
      status.aiProvider = 'Groq API';
      status.aiProviderModel = 'llama-3.1-8b-instant';
    } else {
      status.aiProvider = 'Ollama';
      status.aiProviderModel = 'llama3.2:3b';
    }
  }

  return status;
}

/**
 * Get provider information for status response
 */
export async function getProviderInfo() {
  const groqEnabled = isGroqEnabled();
  const groqAvailable = groqEnabled ? await isGroqAvailable() : false;

  return {
    groqEnabled,
    groqAvailable,
    ollamaRecommended: !groqAvailable,
    note: groqAvailable
      ? 'Using Groq API (fast, sub-second responses)'
      : groqEnabled
        ? 'Groq enabled but not available. Using Ollama fallback (free, local).'
        : 'Using Ollama (free, local). Enable Groq by setting USE_GROQ=true and GROQ_API_KEY=your-key for faster processing.',
  };
}

/**
 * Normalize recipe data for sharing (ensures ingredient_name is present).
 */
export function normalizeRecipeForShare(recipe: Record<string, any>) {
  return {
    ...recipe,
    recipe_ingredients: (recipe.recipe_ingredients || []).map(
      (ri: Record<string, Record<string, unknown>>) => ({
        ...ri,
        ingredients: {
          ...ri.ingredients,
          ingredient_name: ri.ingredients?.ingredient_name || ri.ingredients?.name,
        },
      }),
    ),
  };
}

import type { CreateShiftInput } from './types';

/**
 * Validates shift request data.
 */
export function validateShiftRequest(body: Record<string, any>): {
  isValid: boolean;
  error?: string;
  data?: Partial<CreateShiftInput>;
} {
  if (!body.employee_id) {
    return { isValid: false, error: 'Employee ID is required' };
  }

  if (!body.shift_date) {
    return { isValid: false, error: 'Shift date is required' };
  }

  if (!body.start_time) {
    return { isValid: false, error: 'Start time is required' };
  }

  if (!body.end_time) {
    return { isValid: false, error: 'End time is required' };
  }

  // Validate date format
  const dateRegex = /^\d{4}-\d{2}-\d{2}$/;
  if (!dateRegex.test(body.shift_date)) {
    return { isValid: false, error: 'Invalid date format. Use YYYY-MM-DD' };
  }

  // Validate timestamps
  const startTime = new Date(body.start_time);
  const endTime = new Date(body.end_time);

  if (isNaN(startTime.getTime())) {
    return { isValid: false, error: 'Invalid start time format' };
  }

  if (isNaN(endTime.getTime())) {
    return { isValid: false, error: 'Invalid end time format' };
  }

  if (endTime <= startTime) {
    return { isValid: false, error: 'End time must be after start time' };
  }

  // Validate status if provided
  const validStatuses = ['draft', 'published', 'completed', 'cancelled'];
  if (body.status && !validStatuses.includes(body.status)) {
    return { isValid: false, error: `Invalid status. Must be one of: ${validStatuses.join(', ')}` };
  }

  return {
    isValid: true,
    data: {
      employee_id: body.employee_id,
      template_shift_id: body.template_shift_id || null,
      shift_date: body.shift_date,
      start_time: body.start_time,
      end_time: body.end_time,
      status: body.status || 'draft',
      role: body.role || null,
      break_duration_minutes: body.break_duration_minutes || 0,
      notes: body.notes || null,
    },
  };
}

/**
 * Sync operation handler for Square sync API
 */

import { ApiErrorHandler } from '@/lib/api-error-handler';
import {
  syncCatalogBidirectional,
  syncCatalogFromSquare,
  syncCatalogToSquare,
} from '@/lib/square/sync/catalog';
import { syncCostsToSquare } from '@/lib/square/sync/costs';
import { performInitialSync } from '@/lib/square/sync/initial-sync';
import { syncOrdersFromSquare, syncRecentOrdersFromSquare } from '@/lib/square/sync/orders';
import {
  syncStaffBidirectional,
  syncStaffFromSquare,
  syncStaffToSquare,
} from '@/lib/square/sync/staff';
import { NextResponse } from 'next/server';

export async function handleSyncOperation(
  operation: 'catalog' | 'orders' | 'staff' | 'costs' | 'initial_sync',
  direction: 'from_square' | 'to_square' | 'bidirectional' | undefined,
  options: Record<string, any> | undefined,
  userId: string,
) {
  switch (operation) {
    case 'catalog':
      if (direction === 'from_square') {
        return await syncCatalogFromSquare(userId, options?.locationId);
      } else if (direction === 'to_square') {
        return await syncCatalogToSquare(userId, options?.dishIds);
      } else {
        return await syncCatalogBidirectional(userId);
      }

    case 'orders':
      if (options?.startDate && options?.endDate) {
        return await syncOrdersFromSquare(
          userId,
          options.startDate,
          options.endDate,
          options?.locationId,
        );
      } else {
        return await syncRecentOrdersFromSquare(userId, options?.days || 30, options?.locationId);
      }

    case 'staff':
      if (direction === 'from_square') {
        return await syncStaffFromSquare(userId);
      } else if (direction === 'to_square') {
        return await syncStaffToSquare(userId, options?.employeeIds);
      } else {
        return await syncStaffBidirectional(userId);
      }

    case 'costs':
      return await syncCostsToSquare(userId, options?.dishIds);

    case 'initial_sync': {
      const { getSquareConfig } = await import('@/lib/square/config');
      const config = await getSquareConfig(userId);
      if (!config) {
        throw NextResponse.json(
          ApiErrorHandler.createError('Square configuration not found', 'CONFIG_NOT_FOUND', 404),
          { status: 404 },
        );
      }
      return await performInitialSync(userId, config);
    }

    default:
      throw NextResponse.json(
        ApiErrorHandler.createError(
          `Unknown sync operation: ${operation}`,
          'INVALID_OPERATION',
          400,
        ),
        { status: 400 },
      );
  }
}

/**
 * Build supplier data object from request body.
 *
 * @param {Object} body - Request body
 * @returns {Object} Supplier data object
 */
export function buildSupplierData(body: unknown) {
  const data = body as Record<string, any>;
  return {
    supplier_name: data.supplier_name,
    contact_person: data.contact_person || null,
    email: data.email || null,
    phone: data.phone || null,
    address: data.address || null,
    website: data.website || null,
    payment_terms: data.payment_terms || null,
    delivery_schedule: data.delivery_schedule || null,
    minimum_order_amount: data.minimum_order_amount ? parseFloat(data.minimum_order_amount) : null,
    notes: data.notes || null,
    is_active: data.is_active !== undefined ? data.is_active : true,
  };
}

import { ApiErrorHandler } from '@/lib/api-error-handler';
import { logger } from '@/lib/logger';
import { SupabaseClient } from '@supabase/supabase-js';
import { NextRequest, NextResponse } from 'next/server';
import { detectTemperatureThresholds } from '../../helpers/detectTemperatureThresholds';
import { handleTemperatureEquipmentError } from '../../helpers/handleTemperatureEquipmentError';
import { updateTemperatureEquipmentSchema } from '../../helpers/schemas';

export async function handleUpdateTemperatureEquipment(
  supabase: SupabaseClient,
  request: NextRequest,
  id: string,
) {
  try {

    if (!id) {
      return NextResponse.json(
        ApiErrorHandler.createError('Equipment ID is required', 'VALIDATION_ERROR', 400),
        { status: 400 },
      );
    }

    let body: unknown;
    try {
      body = await request.json();
    } catch (err) {
      logger.warn('[Temperature Equipment API] Failed to parse request body:', {
        error: err instanceof Error ? err.message : String(err),
      });
      return NextResponse.json(
        ApiErrorHandler.createError('Invalid request body', 'VALIDATION_ERROR', 400),
        { status: 400 },
      );
    }

    const validationResult = updateTemperatureEquipmentSchema.safeParse(body);
    if (!validationResult.success) {
      return NextResponse.json(
        ApiErrorHandler.createError(
          validationResult.error.issues[0]?.message || 'Invalid request body',
          'VALIDATION_ERROR',
          400,
        ),
        { status: 400 },
      );
    }

    const validatedBody = validationResult.data;

    // Get current equipment to use name/type for threshold detection if needed
    const { data: currentEquipment, error: fetchError } = await supabase
      .from('temperature_equipment')
      .select('name, equipment_type')
      .eq('id', id)
      .single();

    if (fetchError) {
      logger.error('[Temperature Equipment API] Error fetching equipment for update:', {
        error: fetchError.message,
        equipmentId: id,
      });
      const apiError = ApiErrorHandler.fromSupabaseError(fetchError, 500);
      return NextResponse.json(apiError, { status: apiError.status || 500 });
    }

    // Automatically detect thresholds if not provided and name/type changed
    let minTemp = validatedBody.min_temp_celsius ?? null;
    let maxTemp = validatedBody.max_temp_celsius ?? null;

    const name = validatedBody.name || currentEquipment?.name || '';
    const equipmentType = validatedBody.equipment_type || currentEquipment?.equipment_type || '';

    // Auto-detect if thresholds are null and we have name/type
    if ((minTemp === null || maxTemp === null) && (name || equipmentType)) {
      const detectedThresholds = detectTemperatureThresholds(name, equipmentType);
      if (minTemp === null) {
        minTemp = detectedThresholds.min_temp_celsius;
      }
      if (maxTemp === null) {
        maxTemp = detectedThresholds.max_temp_celsius;
      }
      logger.dev('[Temperature Equipment API] Auto-detected thresholds on update:', {
        id,
        name,
        equipment_type: equipmentType,
        detected: detectedThresholds,
        applied: { min_temp_celsius: minTemp, max_temp_celsius: maxTemp },
      });
    }

    const updateData: Record<string, any> = {
      updated_at: new Date().toISOString(),
    };

    if (validatedBody.name !== undefined) updateData.name = validatedBody.name;
    if (validatedBody.equipment_type !== undefined)
      updateData.equipment_type = validatedBody.equipment_type;
    if (validatedBody.location !== undefined) updateData.location = validatedBody.location;
    if (minTemp !== undefined) updateData.min_temp_celsius = minTemp;
    if (maxTemp !== undefined) updateData.max_temp_celsius = maxTemp;
    if (validatedBody.is_active !== undefined) updateData.is_active = validatedBody.is_active;

    const { data, error } = await supabase
      .from('temperature_equipment')
      .update(updateData)
      .eq('id', id)
      .select()
      .single();

    if (error) {
      logger.error('[Temperature Equipment API] Database error updating equipment:', {
        error: error.message,
        code: error.code,
        context: {
          endpoint: `/api/temperature-equipment/${id}`,
          operation: 'PUT',
          equipmentId: id,
        },
      });

      const apiError = ApiErrorHandler.fromSupabaseError(error, 500);
      return NextResponse.json(apiError, { status: apiError.status || 500 });
    }

    return NextResponse.json({ success: true, data });
  } catch (err: unknown) {
    logger.error('[Temperature Equipment API] Unexpected error updating equipment:', {
      error: err instanceof Error ? err.message : String(err),
      stack: err instanceof Error ? err.stack : undefined,
    });

    return handleTemperatureEquipmentError(err, 'PUT');
  }
}

/**
 * Apply Queensland food safety standards to equipment data.
 *
 * @param {Array} equipment - Equipment data array
 * @returns {Array} Equipment data with Queensland-compliant thresholds
 */
export function applyQueenslandStandards(equipment: Record<string, any>[]): Record<string, any>[] {
  return equipment.map(eq => {
    const name = eq.name.toLowerCase();

    // Apply Queensland thresholds based on equipment type
    if (name.includes('freezer') || name.includes('frozen')) {
      return {
        ...eq,
        min_temp_celsius: -24, // Optimal minimum freezer temperature
        max_temp_celsius: -18, // Queensland freezer standard - must be at or below -18C
      };
    } else if (name.includes('hot') || name.includes('warming') || name.includes('steam')) {
      return {
        ...eq,
        min_temp_celsius: 60, // Queensland hot holding standard
        max_temp_celsius: null, // No upper limit for hot holding
      };
    } else {
      // Default to cold storage (fridges, walk-ins, etc.)
      // Set 0C to 5C range for optimal food safety
      return {
        ...eq,
        min_temp_celsius: 0, // Minimum temperature for cold storage
        max_temp_celsius: 5, // Queensland cold storage standard - must be at or below 5C
      };
    }
  });
}

/**
 * Helper function to load an image and get its dimensions
 */

interface Feature {
  title: string;
  description: string;
  screenshot: string;
  screenshotAlt: string;
}

export function loadImage(
  imageSrc: string,
  onLoad: (dimensions: { width: number; height: number } | null) => void,
  onError?: () => void,
): void {
  const img = new window.Image();
  img.src = imageSrc;
  img.onload = () => {
    if (img.naturalWidth && img.naturalHeight) {
      onLoad({ width: img.naturalWidth, height: img.naturalHeight });
    } else {
      onLoad(null);
    }
  };
  img.onerror = () => {
    onLoad(null);
    onError?.();
  };
}

