/**
 * Hook for comprehensive scraping handlers
 */

import { useNotification } from '@/contexts/NotificationContext';
import { logger } from '@/lib/logger';
import { useCallback } from 'react';
import { ComprehensiveJobStatus } from '../types';
import { createStopHandler } from './useComprehensiveScrapingHandlers/stop-handler';

interface UseComprehensiveScrapingHandlersParams {
  setComprehensiveScraping: (value: boolean) => void;
  setComprehensiveStatus: (status: ComprehensiveJobStatus | null) => void;
  setStatusPolling: (polling: boolean) => void;
  fetchComprehensiveStatus: () => Promise<void>;
}

export function useComprehensiveScrapingHandlers({
  setComprehensiveScraping,
  setComprehensiveStatus,
  setStatusPolling,
  fetchComprehensiveStatus: _fetchComprehensiveStatus,
}: UseComprehensiveScrapingHandlersParams) {
  const { showSuccess, showError } = useNotification();

  const handleComprehensiveScrape = useCallback(async () => {
    setComprehensiveScraping(true);
    try {
      const response = await fetch('/api/recipe-scraper/scrape', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ comprehensive: true }),
      });
      const result = (await response.json()) as {
        success: boolean;
        data: { jobStatus: ComprehensiveJobStatus };
        message?: string;
      };

      if (result.success) {
        setComprehensiveStatus(result.data.jobStatus);
        setStatusPolling(true);
        showSuccess(
          'Comprehensive scraping job started! This will scrape ALL recipes from all sources.',
        );
      } else {
        showError(result.message || 'Failed to start comprehensive scraping');
      }
    } catch (err) {
      logger.error('[RecipeScraper] Error starting comprehensive scrape:', {
        error: err instanceof Error ? err.message : String(err),
      });
      showError('Failed to start comprehensive scraping');
    } finally {
      setComprehensiveScraping(false);
    }
  }, [setComprehensiveScraping, setComprehensiveStatus, setStatusPolling, showSuccess, showError]);

  const handleStopComprehensiveScrape = useCallback(
    createStopHandler({ setComprehensiveStatus, setStatusPolling, showSuccess, showError }),
    [setComprehensiveStatus, setStatusPolling, showSuccess, showError],
  );

  const handleResumeComprehensiveScrape = useCallback(async () => {
    try {
      showSuccess('Resuming scraper...');

      const response = await fetch('/api/recipe-scraper/resume', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
      });

      if (!response.ok) {
        const errorData = (await response.json().catch(() => ({ error: 'Unknown error' }))) as {
          error?: string;
        };
        throw new Error(errorData.error || `HTTP ${response.status}`);
      }

      const result = (await response.json()) as {
        success: boolean;
        data: { status: ComprehensiveJobStatus };
        message?: string;
      };

      if (result.success) {
        setComprehensiveStatus(result.data.status);
        setStatusPolling(true);
        showSuccess('Scraping job resumed! Continuing from saved progress.');
      } else {
        showError(result.message || 'Failed to resume scraping job');
      }
    } catch (err) {
      logger.error('[RecipeScraper] Error resuming comprehensive scrape:', {
        error: err instanceof Error ? err.message : String(err),
      });
      showError(
        `Failed to resume scraping job: ${err instanceof Error ? err.message : String(err)}`,
      );
    }
  }, [setComprehensiveStatus, setStatusPolling, showSuccess, showError]);

  const handleRefreshStatus = useCallback(async () => {
    try {
      const response = await fetch('/api/recipe-scraper/status');
      const result = (await response.json()) as { success: boolean; data: ComprehensiveJobStatus };
      if (result.success) {
        setComprehensiveStatus(result.data);
        if (result.data.isRunning) {
          setStatusPolling(true);
        }
        showSuccess('Status refreshed');
      }
    } catch (err) {
      logger.error('[RecipeScraper] Error refreshing status:', {
        error: err instanceof Error ? err.message : String(err),
      });
      showError('Failed to refresh status');
    }
  }, [setComprehensiveStatus, setStatusPolling, showSuccess, showError]);

  return {
    handleComprehensiveScrape,
    handleStopComprehensiveScrape,
    handleResumeComprehensiveScrape,
    handleRefreshStatus,
  };
}

import { flushSync } from 'react-dom';
import { createCalculation } from '../utils/createCalculation';
import { Ingredient, COGSCalculation } from '../../types';

interface NewIngredient {
  ingredient_id?: string;
  quantity?: number;
  unit?: string;
}

interface ProcessIngredientProps {
  newIngredient: { ingredient_id: string; quantity: number; unit?: string };
  selectedIngredientData: Ingredient;
  selectedRecipe: string | null;
  convertIngredientQuantity: (
    quantity: number,
    fromUnit: string,
    toUnit: string,
  ) => {
    convertedQuantity: number;
    convertedUnit: string;
    conversionNote?: string;
  };
  existingIngredient: COGSCalculation | undefined;
  currentCalculations: COGSCalculation[];
  updateCalculation: (ingredientId: string, quantity: number) => void;
  addCalculation: (calc: COGSCalculation) => void;
  resetForm: () => void;
}

/**
 * Process ingredient addition or update.
 *
 * @param {ProcessIngredientProps} props - Processing props
 */
export function processIngredientAddition({
  newIngredient,
  selectedIngredientData,
  selectedRecipe,
  convertIngredientQuantity,
  existingIngredient,
  currentCalculations,
  updateCalculation,
  addCalculation,
  resetForm,
}: ProcessIngredientProps): void {
  const { convertedQuantity, convertedUnit, conversionNote } = convertIngredientQuantity(
    newIngredient.quantity!,
    newIngredient.unit || 'kg',
    selectedIngredientData.unit || 'kg',
  );

  if (existingIngredient) {
    const currentCalc = currentCalculations.find(
      calc => calc.ingredientId === newIngredient.ingredient_id,
    );
    if (currentCalc) {
      flushSync(() =>
        updateCalculation(newIngredient.ingredient_id!, currentCalc.quantity + convertedQuantity),
      );
      resetForm();
    }
    return;
  }

  const newCalc = createCalculation(
    newIngredient.ingredient_id!,
    selectedIngredientData,
    convertedQuantity,
    convertedUnit,
    conversionNote || '',
    selectedRecipe,
  );
  flushSync(() => {
    addCalculation(newCalc);
  });
  resetForm();
}

/**
 * Hook for category auto-detection logic.
 */

import { autoDetectCategory } from '@/lib/ingredients/category-detection';
import { logger } from '@/lib/logger';
import { useCallback, useEffect, useRef, useState } from 'react';

interface FormData {
  ingredient_name?: string;
  brand?: string;
  storage_location?: string;
  category?: string;
}

export function useCategoryDetection(formData: FormData) {
  const [autoDetectedCategory, setAutoDetectedCategory] = useState<string | null>(null);
  const detectionTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  async function performCategoryDetection(
    name: string,
    brand: string | undefined,
    storage: string | undefined,
    setFormData: React.Dispatch<React.SetStateAction<Partial<FormData>>>,
    setCategoryState: (cat: string) => void,
  ) {
    try {
      const { category } = await autoDetectCategory(
        name,
        brand,
        storage,
        true, // use AI
      );

      if (!category) return;

      // Only update if category is still empty (user hasn't manually set it)
      setFormData(prev => {
        if (!prev.category) {
          setCategoryState(category);
          return { ...prev, category };
        }
        return prev;
      });
    } catch (error) {
      logger.error('Error auto-detecting category:', error);
    }
  }

  const handleCategoryDetection = useCallback(
    (ingredientName: string, setFormData: React.Dispatch<React.SetStateAction<Partial<FormData>>>) => {
      // Clear existing timeout
      if (detectionTimeoutRef.current) {
        clearTimeout(detectionTimeoutRef.current);
      }

      // Early exit if condition not met
      const currentCategory = formData.category;
      if (currentCategory || ingredientName.trim().length <= 2) {
        return;
      }

      const { brand, storage_location } = formData;

      detectionTimeoutRef.current = setTimeout(() => {
        performCategoryDetection(
          ingredientName,
          brand,
          storage_location,
          setFormData,
          setAutoDetectedCategory,
        );
      }, 500); // 500ms debounce
    },
    [formData.category, formData.brand, formData.storage_location],
  );

  const clearAutoDetectedCategory = useCallback(() => {
    setAutoDetectedCategory(null);
  }, []);

  // Cleanup timeout on unmount
  useEffect(() => {
    return () => {
      if (detectionTimeoutRef.current) {
        clearTimeout(detectionTimeoutRef.current);
      }
    };
  }, []);

  return {
    autoDetectedCategory,
    handleCategoryDetection,
    clearAutoDetectedCategory,
    setAutoDetectedCategory,
  };
}

import { useSmartLoading } from '@/hooks/useSmartLoading';
import { supabase } from '@/lib/supabase';
import { useEffect, useState } from 'react';

import { logger } from '@/lib/logger';
interface Ingredient {
  id: string;
  ingredient_name: string;
}

interface Supplier {
  id: string;
  supplier_name?: string; // Actual column name in database
  name?: string; // Fallback for compatibility
  created_at?: string;
}

export function useIngredientData() {
  const [suppliers, setSuppliers] = useState<Supplier[]>([]);
  const [loading, setSmartLoading] = useSmartLoading(false, 200);
  const [error, setError] = useState<string | null>(null);

  const fetchSuppliers = async () => {
    try {
      setSmartLoading(true);
      // Try supplier_name first (actual column name), fallback to name if that fails
      const { data, error } = await supabase
        .from('suppliers')
        .select('*')
        .order('supplier_name', { ascending: true });

      if (error) {
        // Fallback: try 'name' column if supplier_name doesn't exist
        const fallbackQuery = await supabase
          .from('suppliers')
          .select('*')
          .order('name', { ascending: true });

        if (fallbackQuery.error) {
          logger.error('Error fetching suppliers:', fallbackQuery.error);
          setError(`Failed to load suppliers: ${fallbackQuery.error.message}`);
          throw fallbackQuery.error;
        }

        setSuppliers(fallbackQuery.data || []);
      } else {
        setSuppliers(data || []);
      }
    } catch (error: unknown) {
      logger.error('Error fetching suppliers:', error);
      const errorMessage = error instanceof Error ? error.message : 'Failed to load suppliers';
      setError(errorMessage);
      setSuppliers([]); // Set empty array on error to prevent crashes
    } finally {
      setSmartLoading(false);
    }
  };

  useEffect(() => {
    fetchSuppliers();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return {
    suppliers,
    loading,
    error,
    setError,
    fetchSuppliers,
  };
}

import { useState, useEffect, useRef } from 'react';

interface UseMenuItemDropdownsProps {
  onMoveToCategory?: (targetCategory: string) => void;
  currentCategory: string;
  availableCategories?: string[];
}

/**
 * Hook for managing menu item dropdown state and handlers.
 *
 * @param {UseMenuItemDropdownsProps} props - Hook props
 * @returns {Object} Dropdown state and handlers
 */
export function useMenuItemDropdowns({
  onMoveToCategory,
  currentCategory,
  availableCategories = [],
}: UseMenuItemDropdownsProps) {
  const [showCategoryDropdown, setShowCategoryDropdown] = useState(false);
  const [showReorderDropdown, setShowReorderDropdown] = useState(false);
  const categoryDropdownRef = useRef<HTMLDivElement>(null);
  const reorderDropdownRef = useRef<HTMLDivElement>(null);

  // Close dropdowns when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (
        categoryDropdownRef.current &&
        !categoryDropdownRef.current.contains(event.target as Node)
      ) {
        setShowCategoryDropdown(false);
      }
      if (
        reorderDropdownRef.current &&
        !reorderDropdownRef.current.contains(event.target as Node)
      ) {
        setShowReorderDropdown(false);
      }
    };

    if (showCategoryDropdown || showReorderDropdown) {
      document.addEventListener('mousedown', handleClickOutside);
      return () => document.removeEventListener('mousedown', handleClickOutside);
    }
  }, [showCategoryDropdown, showReorderDropdown]);

  const handleMoveToCategory = (targetCategory: string) => {
    if (onMoveToCategory && targetCategory !== currentCategory) {
      onMoveToCategory(targetCategory);
    }
    setShowCategoryDropdown(false);
  };

  return {
    showCategoryDropdown,
    setShowCategoryDropdown,
    showReorderDropdown,
    setShowReorderDropdown,
    categoryDropdownRef,
    reorderDropdownRef,
    handleMoveToCategory,
  };
}

/**
 * Format menu for printing
 *
 * Generates HTML structure for printable menu with items, descriptions, prices, allergens, and allergen matrix
 */

import type { Menu, MenuItem } from '../types';
import { AUSTRALIAN_ALLERGENS } from '@/lib/allergens/australian-allergens';
import { formatCategorySection } from './formatMenuForPrint/helpers/formatCategorySection';
import { generateAllergenMatrix } from './formatMenuForPrint/helpers/generateAllergenMatrix';

const _VALID_ALLERGEN_CODES = AUSTRALIAN_ALLERGENS.map(a => a.code);

/**
 * Escape HTML special characters.
 *
 * @param {string} text - Text to escape
 * @returns {string} Escaped HTML text
 */
function escapeHtml(text: string): string {
  const map: Record<string, string> = {
    '&': '&amp;',
    '<': '&lt;',
    '>': '&gt;',
    '"': '&quot;',
    "'": '&#039;',
  };
  return text.replace(/[&<>"']/g, m => map[m]);
}

/**
 * Format menu for printing.
 *
 * Uses existing descriptions only - no AI generation (for instant printing).
 *
 * @param {Menu} menu - Menu object
 * @param {MenuItem[]} menuItems - Array of menu items (should include existing descriptions)
 * @returns {string} HTML string for printing
 */
export function formatMenuForPrint(
  menu: Menu,
  menuItems: MenuItem[],
  variant: 'default' | 'customer' = 'default',
): string {
  // Return content HTML only - template provides header, logo, background elements
  const variantClass = variant === 'customer' ? 'variant-customer' : '';
  let html = `<div class="menu-print ${variantClass}">`;

  // Group items by category
  const itemsByCategory = new Map<string, MenuItem[]>();
  menuItems.forEach(item => {
    const category = item.category || 'Uncategorized';
    if (!itemsByCategory.has(category)) {
      itemsByCategory.set(category, []);
    }
    itemsByCategory.get(category)!.push(item);
  });

  // Sort categories alphabetically
  const sortedCategories = Array.from(itemsByCategory.keys()).sort();

  sortedCategories.forEach(category => {
    html += formatCategorySection(category, itemsByCategory.get(category)!);
  });

  html += generateAllergenMatrix(menuItems);
  html += `</div>`;

  return html;
}

'use client';

import { useState } from 'react';
import { useTranslation } from '@/lib/useTranslation';
import { Icon } from '@/components/ui/Icon';
import { X, Printer } from 'lucide-react';
import { usePrepListExport } from '../hooks/usePrepListExport';
import type { PrepList, SectionData, KitchenSection } from '../types';

interface PrepListExportProps {
  prepList?: PrepList;
  generatedData?: {
    sections: SectionData[];
    menuName: string;
  };
  kitchenSections?: KitchenSection[];
  onClose: () => void;
}

export function PrepListExport({
  prepList,
  generatedData,
  kitchenSections = [],
  onClose,
}: PrepListExportProps) {
  const { t } = useTranslation();
  const { printPrepList, printGeneratedPrepList } = usePrepListExport();
  const [selectedSections, setSelectedSections] = useState<string[]>([]);
  const [includeInstructions, setIncludeInstructions] = useState(true);

  const handlePrint = () => {
    if (prepList) {
      printPrepList(prepList);
    } else if (generatedData) {
      const options = {
        sections: selectedSections.length > 0 ? selectedSections : undefined,
        includeInstructions,
      };
      printGeneratedPrepList(generatedData.sections, generatedData.menuName, options);
    }
    onClose();
  };

  const toggleSection = (sectionId: string | null) => {
    if (!sectionId) return;
    setSelectedSections(prev =>
      prev.includes(sectionId) ? prev.filter(id => id !== sectionId) : [...prev, sectionId],
    );
  };

  const selectAllSections = () => {
    if (!generatedData) return;
    const allSectionIds = generatedData.sections
      .map(s => s.sectionId)
