import { ImportProgress, type ImportProgressState } from './ImportProgress';

export interface CSVImportConfig<T = any> {
  /** Entity type name (e.g., "Ingredients", "Recipes") */
  entityName: string;
  /** Entity name plural (e.g., "ingredients", "recipes") */
  entityNamePlural: string;
  /** CSV column headers expected */
  expectedColumns: string[];
  /** Optional columns that may be present */
  optionalColumns?: string[];
  /** Parse CSV text into entity objects */
  parseCSV: (csvText: string) => ParseCSVResult<T>;
  /** Validate parsed entity */
  validateEntity?: (entity: T, index: number) => { valid: boolean; error?: string };
  /** Format entity for preview display */
  formatEntityForPreview: (entity: T, index: number) => React.ReactNode;
  /** Generate template CSV content */
  generateTemplate: () => string;
  /** Template filename */
  templateFilename: string;
  /** Instructions for CSV format */
  instructions: string[];
}

export interface CSVImportModalProps<T = any> {
  /** Whether modal is open */
  isOpen: boolean;
  /** Close handler */
  onClose: () => void;
  /** Import handler - receives selected entities */
  onImport: (entities: T[]) => Promise<void>;
  /** Import configuration */
  config: CSVImportConfig<T>;
  /** Loading state */
  loading?: boolean;
  /** Import progress state */
  progress?: ImportProgressState;
}

/**
 * Standardized CSV import modal
 * @component
 * @param {CSVImportModalProps} props - Component props
 * @returns {JSX.Element} CSV import modal component
 */
export function CSVImportModal<T = any>({
  isOpen,
  onClose,
  onImport,
  config,
  loading = false,
  progress,
}: CSVImportModalProps<T>) {
  const [_csvData, setCsvData] = useState<string>('');
  const [parsedEntities, setParsedEntities] = useState<T[]>([]);
  const [selectedIndices, setSelectedIndices] = useState<Set<number>>(new Set());
  const [errors, setErrors] = useState<Array<{ row: number; error: string }>>([]);
  const [validationErrors, setValidationErrors] = useState<Map<number, string>>(new Map());

  const parseCSVData = useCallback(
    (csvText: string) => {
      try {
        if (!csvText || csvText.trim().length === 0) {
          setErrors([{ row: 0, error: 'CSV file is empty' }]);
          setParsedEntities([]);
          setSelectedIndices(new Set());
          setValidationErrors(new Map());
          return;
        }

        const result = config.parseCSV(csvText);
        const parseErrors = result.errors.map(err => ({
          row: err.row || 0,
          error: err.message || 'Parse error',
        }));
        setErrors(parseErrors);
        const newValidationErrors = new Map<number, string>();
        if (config.validateEntity && result.data.length > 0) {
          result.data.forEach((entity, index) => {
            const validation = config.validateEntity!(entity, index);
            if (!validation.valid && validation.error)
              newValidationErrors.set(index, validation.error);
          });
        }
        setValidationErrors(newValidationErrors);
        setParsedEntities(result.data);
        const validIndices = new Set<number>();
        result.data.forEach((_, index) => {
          if (!newValidationErrors.has(index)) validIndices.add(index);
        });
        setSelectedIndices(validIndices);
      } catch (err) {
        logger.error('[CSV Import Modal] Failed to parse CSV:', err);
        setErrors([
          { row: 0, error: err instanceof Error ? err.message : 'Failed to parse CSV file' },
        ]);
        setParsedEntities([]);
        setSelectedIndices(new Set());
        setValidationErrors(new Map());
      }
    },
    [config],
  );

  const handleFileUpload = useCallback(
    (event: React.ChangeEvent<HTMLInputElement>) => {
      const file = event.target.files?.[0];
      if (!file) return;

      const reader = new FileReader();
      reader.onload = e => {
        const csvText = e.target?.result as string;
        setCsvData(csvText);
        parseCSVData(csvText);
      };
      reader.readAsText(file);
    },
    [parseCSVData],
  );

  const handleSelectEntity = useCallback((index: number, selected: boolean) => {
    setSelectedIndices(prev => {
      const newSet = new Set(prev);
      if (selected) {
        newSet.add(index);
      } else {
        newSet.delete(index);
      }
      return newSet;
    });
  }, []);

  const handleSelectAll = useCallback(
    (selected: boolean) => {
      if (selected) {
        // Select all valid entities
        const validIndices = new Set<number>();
        parsedEntities.forEach((_, index) => {
          if (!validationErrors.has(index)) {
            validIndices.add(index);
          }
        });
        setSelectedIndices(validIndices);
      } else {
        setSelectedIndices(new Set());
      }
    },
    [parsedEntities, validationErrors],
  );

  const handleImport = useCallback(async () => {
    try {
      const entitiesToImport = parsedEntities.filter((_, index) => selectedIndices.has(index));

      if (entitiesToImport.length === 0) {
        return;
      }

      await onImport(entitiesToImport);

      // Reset state on success
      setCsvData('');
      setParsedEntities([]);
      setSelectedIndices(new Set());
      setErrors([]);
      setValidationErrors(new Map());
    } catch (err) {
      logger.error(`[CSV Import Modal] Failed to import ${config.entityNamePlural}:`, err);
    }
  }, [parsedEntities, selectedIndices, onImport, config.entityNamePlural]);

  const handleClose = useCallback(() => {
    setCsvData('');
    setParsedEntities([]);
    setSelectedIndices(new Set());
    setErrors([]);
    setValidationErrors(new Map());
    onClose();
  }, [onClose]);

  const handleDownloadTemplate = useCallback(() => {
    const template = config.generateTemplate();
    const blob = new Blob([template], { type: 'text/csv;charset=utf-8;' });
    const url = window.URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = config.templateFilename;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    window.URL.revokeObjectURL(url);
  }, [config]);

  const selectedValidCount = Array.from(selectedIndices).filter(
    i => !validationErrors.has(i),
  ).length;
  const validCount = parsedEntities.length - validationErrors.size;

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-[80] flex items-center justify-center bg-black/60 p-4 backdrop-blur-sm">
      <div className="max-h-[90vh] w-full max-w-4xl rounded-3xl bg-gradient-to-r from-[var(--primary)]/20 via-[var(--accent)]/20 via-[var(--tertiary)]/20 to-[var(--primary)]/20 p-[1px] shadow-2xl">
        <div className="max-h-[90vh] w-full overflow-y-auto rounded-3xl bg-[var(--surface)]/95">
          {/* Header */}
          <div className="desktop:p-6 border-b border-[var(--border)] p-4">
            <div className="flex items-center justify-between">
              <div>
                <h2 className="desktop:text-2xl text-xl font-bold text-[var(--foreground)]">
                  Import {config.entityName} from CSV
                </h2>
                <p className="mt-1 text-sm text-[var(--foreground)]/60">
                  Upload a CSV file or download our template to get started
                </p>
              </div>
              <button
                onClick={handleClose}
                className="rounded-full bg-[var(--muted)] p-2 text-[var(--foreground)]/60 transition-colors hover:bg-[var(--border)] hover:text-[var(--foreground)]"
                aria-label="Close import modal"
              >
                <Icon icon={X} size="md" aria-hidden={true} />
              </button>
            </div>
          </div>

          {/* Content */}
          <div className="desktop:space-y-6 desktop:p-6 space-y-4 p-4">
            {/* Template Download */}
            <CSVImportTemplateDownload onDownload={handleDownloadTemplate} />

            {/* File Upload */}
            <CSVImportFileUpload
              expectedColumns={config.expectedColumns}
              optionalColumns={config.optionalColumns}
              onFileUpload={handleFileUpload}
            />

            {/* Errors Display */}
            <CSVImportErrors parseErrors={errors} validationErrors={validationErrors} />

            {/* Progress Indicator */}
            {loading && progress && (
              <ImportProgress
                progress={progress}
                title={`Importing ${config.entityNamePlural}...`}
              />
            )}

            {/* Preview */}
            {parsedEntities.length > 0 && !loading && (
              <>
                <CSVImportPreview
                  parsedEntities={parsedEntities}
                  selectedIndices={selectedIndices}
                  validationErrors={validationErrors}
                  entityName={config.entityName}
                  entityNamePlural={config.entityNamePlural}
                  formatEntityForPreview={config.formatEntityForPreview}
                  onSelectEntity={handleSelectEntity}
                  onSelectAll={handleSelectAll}
                />

                <CSVImportActions
                  selectedValidCount={selectedValidCount}
                  validCount={validCount}
                  entityNamePlural={config.entityNamePlural}
                  loading={loading}
                  onClose={handleClose}
                  onImport={handleImport}
                />
              </>
            )}
            <CSVImportInstructions instructions={config.instructions} />
          </div>
        </div>
      </div>
    </div>
  );
}

'use client';

import { Icon } from '@/components/ui/Icon';
import { useMediaQuery } from '@/hooks/useMediaQuery';
import { X } from 'lucide-react';
import { useEffect, useRef, useState } from 'react';

interface EditDrawerProps {
  isOpen: boolean;
  onClose: () => void;
  title: string;
  children: React.ReactNode;
  footer?: React.ReactNode;
  onSave?: () => void;
  saving?: boolean;
  maxWidth?: 'sm' | 'md' | 'lg' | 'xl' | '2xl' | '3xl' | '4xl';
  preventClose?: boolean; // Prevent closing if form has unsaved changes
}

const maxWidthClasses = {
  sm: 'max-w-sm',
  md: 'max-w-md',
  lg: 'max-w-lg',
  xl: 'max-w-xl',
  '2xl': 'max-w-2xl',
  '3xl': 'max-w-3xl',
  '4xl': 'max-w-4xl',
};

export function EditDrawer({
  isOpen,
  onClose,
  title,
  children,
  footer,
  onSave,
  saving = false,
  maxWidth = 'xl',
  preventClose = false,
}: EditDrawerProps) {
  const drawerRef = useRef<HTMLDivElement>(null);
  const contentRef = useRef<HTMLDivElement>(null);
  const isMobile = useMediaQuery('(max-width: 1024px)'); // desktop: breakpoint is 1025px
  const [swipeStartX, setSwipeStartX] = useState<number | null>(null);
  const [swipeStartY, setSwipeStartY] = useState<number | null>(null);
  const [isSwiping, setIsSwiping] = useState(false);
  const [translateX, setTranslateX] = useState(0);

  // Click outside to close (matching recipe side panel behavior)
  useEffect(() => {
    if (!isOpen || preventClose) return;

    const handleClickOutside = (event: MouseEvent | TouchEvent) => {
      const target = event.target as Element;

      // Don't close if clicking inside the drawer
      if (drawerRef.current?.contains(target)) {
        return;
      }

      // Don't close if clicking on modals or dialogs
      if (target.closest('[role="dialog"]') || target.closest('[aria-modal="true"]')) {
        return;
      }

      // Don't close if clicking on dropdowns or menus
      if (target.closest('[role="menu"]') || target.closest('[role="listbox"]')) {
        return;
      }

      // Close the drawer
      onClose();
    };

    // Use mousedown instead of click to catch clicks before they bubble
    document.addEventListener('mousedown', handleClickOutside);
    document.addEventListener('touchstart', handleClickOutside);

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('touchstart', handleClickOutside);
    };
  }, [isOpen, onClose, preventClose]);

  // Close on Escape key
  useEffect(() => {
    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === 'Escape' && isOpen && !preventClose) {
        onClose();
      }
    };
    document.addEventListener('keydown', handleEscape);
    return () => document.removeEventListener('keydown', handleEscape);
  }, [isOpen, onClose, preventClose]);

  // Cmd+S / Ctrl+S to save
  useEffect(() => {
    const handleSave = (e: KeyboardEvent) => {
      if ((e.metaKey || e.ctrlKey) && e.key === 's' && isOpen && onSave) {
        e.preventDefault();
        onSave();
      }
    };
    document.addEventListener('keydown', handleSave);
    return () => document.removeEventListener('keydown', handleSave);
  }, [isOpen, onSave]);

  // Prevent body scroll when drawer is open
  useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = '';
      setTranslateX(0);
      setIsSwiping(false);
      setSwipeStartX(null);
      setSwipeStartY(null);
    }
    return () => {
      document.body.style.overflow = '';
    };
  }, [isOpen]);

  // Focus first input when drawer opens
  useEffect(() => {
    if (isOpen && contentRef.current) {
      // Small delay to ensure drawer animation has started
      setTimeout(() => {
        const firstInput = contentRef.current?.querySelector(
          'input:not([type="hidden"]), textarea, select',
        ) as HTMLElement;
        if (firstInput) {
          firstInput.focus();
          // Scroll input into view on mobile
          if (isMobile) {
            firstInput.scrollIntoView({ behavior: 'smooth', block: 'center' });
          }
        }
      }, 100);
    }
  }, [isOpen, isMobile]);

  // Swipe-to-close gesture (right-side drawer, swipe right to close)
  const handleTouchStart = (e: React.TouchEvent) => {
    if (!isMobile) return;
    setSwipeStartX(e.touches[0].clientX);
    setSwipeStartY(e.touches[0].clientY);
    setIsSwiping(true);
  };

  const handleTouchMove = (e: React.TouchEvent) => {
    if (!isMobile || !isSwiping || swipeStartX === null || swipeStartY === null) return;

    const currentX = e.touches[0].clientX;
    const currentY = e.touches[0].clientY;
    const deltaX = currentX - swipeStartX;
    const deltaY = Math.abs(currentY - swipeStartY);

    // Only allow swipe if it's primarily horizontal (swipe right to close)
    // And prevent if scrolling vertically
    if (deltaX > 0 && deltaX > deltaY * 1.5) {
      e.preventDefault();
      setTranslateX(Math.min(deltaX, 400)); // Cap at 400px
    } else if (deltaY > deltaX) {
      // Vertical scroll detected, cancel swipe
      setIsSwiping(false);
      setTranslateX(0);
    }
  };

  const handleTouchEnd = () => {
    if (!isMobile || !isSwiping) return;

    const minSwipeDistance = 100;
    if (translateX > minSwipeDistance && !preventClose) {
      onClose();
    } else {
      // Snap back
      setTranslateX(0);
    }

    setIsSwiping(false);
    setSwipeStartX(null);
    setSwipeStartY(null);
  };

  if (!isOpen) return null;

  const widthClass = isMobile ? 'w-full' : maxWidthClasses[maxWidth] || maxWidthClasses.xl;

  return (
    <>
      {/* Backdrop */}
      <div
        className="fixed inset-0 z-[70] bg-black/60 backdrop-blur-sm transition-opacity duration-200"
        onClick={preventClose ? undefined : onClose}
        aria-hidden={true}
        style={{
          opacity: isSwiping ? Math.max(0.3, 1 - translateX / 400) : 1,
        }}
      />

      {/* Drawer with gradient border */}
      <div
        ref={drawerRef}
        className={`fixed top-0 right-0 z-[75] h-full ${widthClass} transform rounded-l-3xl bg-gradient-to-r from-[var(--primary)]/20 via-[var(--accent)]/20 via-[var(--tertiary)]/20 to-[var(--primary)]/20 p-[1px] shadow-2xl transition-transform duration-200 ease-out ${
          isOpen ? 'translate-x-0' : 'translate-x-full'
        }`}
        role="dialog"
        aria-modal="true"
        aria-labelledby="edit-drawer-title"
        style={{
          transform: `translateX(${isSwiping ? translateX : 0}px)`,
          transition: isSwiping ? 'none' : 'transform 0.2s cubic-bezier(0.4, 0, 0.2, 1)',
        }}
        onTouchStart={handleTouchStart}
        onTouchMove={handleTouchMove}
        onTouchEnd={handleTouchEnd}
      >
        <div className="flex h-full flex-col rounded-l-3xl bg-[var(--surface)]">
          {/* Header */}
          <div
            className="desktop:px-6 desktop:py-4 sticky top-0 z-10 flex items-center justify-between border-b border-[var(--border)] bg-gradient-to-r from-[#1f1f1f] to-[var(--muted)]/50 px-4 py-3 backdrop-blur-sm"
            onTouchStart={handleTouchStart}
            onTouchMove={handleTouchMove}
            onTouchEnd={handleTouchEnd}
          >
            <h2
              id="edit-drawer-title"
              className="desktop:text-xl text-lg font-bold text-[var(--foreground)]"
            >
              {title}
            </h2>
            <button
              onClick={preventClose ? undefined : onClose}
              disabled={preventClose}
              className="rounded-full p-2 text-[var(--foreground-muted)] transition-all duration-200 hover:bg-[var(--muted)] hover:text-[var(--foreground)] focus:ring-2 focus:ring-[var(--primary)] focus:outline-none disabled:cursor-not-allowed disabled:opacity-50"
              aria-label="Close drawer"
            >
              <Icon icon={X} size="md" />
            </button>
          </div>

          {/* Scrollable content */}
          <div
            ref={contentRef}
            className="desktop:px-6 desktop:py-6 flex-1 overflow-y-auto px-4 py-4"
            style={{
              WebkitOverflowScrolling: 'touch',
              overscrollBehavior: 'contain',
            }}
          >
            {children}
          </div>

          {/* Footer */}
          {footer && (
            <div className="desktop:px-6 desktop:py-4 sticky bottom-0 border-t border-[var(--border)] bg-[var(--surface)] px-4 py-3">
              {footer}
            </div>
          )}
        </div>
      </div>
    </>
  );
}

