import { writeFileSync } from 'fs';
import { join } from 'path';
import type { ErrorRecord } from '../fixtures/global-error-listener';

/**
 * Test results summary
 */
export interface TestResultsSummary {
  total: number;
  passed: number;
  failed: number;
  skipped: number;
  duration: number;
  tests: Array<{
    title: string;
    status: 'passed' | 'failed' | 'skipped';
    duration: number;
    error?: string;
    steps?: string[];
  }>;
}

/**
 * Generate QA Audit Report
 */
export function generateQAReport(
  errors: ErrorRecord[],
  testResults: TestResultsSummary,
  outputPath: string = 'QA_AUDIT_REPORT.md',
): void {
  const report: string[] = [];

  // Header
  report.push('# QA Audit Report');
  report.push('');
  report.push(`**Generated:** ${new Date().toISOString()}`);
  report.push('');

  // Summary
  report.push('## Summary');
  report.push('');
  report.push('### Test Results');
  report.push(`- **Total Tests:** ${testResults.total}`);
  report.push(`- **Passed:** ${testResults.passed} `);
  report.push(`- **Failed:** ${testResults.failed} `);
  report.push(`- **Skipped:** ${testResults.skipped} `);
  report.push(`- **Total Duration:** ${(testResults.duration / 1000).toFixed(2)}s`);
  report.push('');

  report.push('### Error Summary');
  report.push(`- **Total Errors Collected:** ${errors.length}`);
  const errorCounts = {
    'console.error': errors.filter(e => e.type === 'console.error').length,
    'console.warn': errors.filter(e => e.type === 'console.warn').length,
    uncaught: errors.filter(e => e.type === 'uncaught').length,
    network: errors.filter(e => e.type === 'network').length,
  };
  report.push(`- **Console Errors:** ${errorCounts['console.error']}`);
  report.push(`- **Console Warnings:** ${errorCounts['console.warn']}`);
  report.push(`- **Uncaught Exceptions:** ${errorCounts['uncaught']}`);
  report.push(`- **Network Errors (4xx/5xx):** ${errorCounts['network']}`);
  report.push('');

  // Failed Tests
  if (testResults.failed > 0) {
    report.push('## Failed Tests');
    report.push('');
    testResults.tests
      .filter(t => t.status === 'failed')
      .forEach(test => {
        report.push(`### ${test.title}`);
        report.push(`- **Status:**  Failed`);
        report.push(`- **Duration:** ${(test.duration / 1000).toFixed(2)}s`);
        if (test.error) {
          report.push(`- **Error:** \`${test.error}\``);
        }
        if (test.steps && test.steps.length > 0) {
          report.push('- **Steps:**');
          test.steps.forEach((step, index) => {
            report.push(`  ${index + 1}. ${step}`);
          });
        }
        report.push('');
      });
  }

  // Error Details
  if (errors.length > 0) {
    report.push('## Error Details');
    report.push('');

    // Group errors by type
    const errorsByType = {
      'console.error': errors.filter(e => e.type === 'console.error'),
      'console.warn': errors.filter(e => e.type === 'console.warn'),
      uncaught: errors.filter(e => e.type === 'uncaught'),
      network: errors.filter(e => e.type === 'network'),
    };

    // Console Errors
    if (errorsByType['console.error'].length > 0) {
      report.push('### Console Errors');
      report.push('');
      errorsByType['console.error'].forEach((error, index) => {
        report.push(`#### Error ${index + 1}`);
        report.push(`- **URL:** ${error.url}`);
        report.push(`- **Message:** \`${error.message}\``);
        if (error.stack) {
          report.push(`- **Stack:** \`\`\``);
          report.push(error.stack);
          report.push(`\`\`\``);
        }
        report.push(`- **Timestamp:** ${error.timestamp}`);
        if (error.screenshot) {
          report.push(`- **Screenshot:** \`${error.screenshot}\``);
        }
        report.push('');
      });
    }

    // Console Warnings
    if (errorsByType['console.warn'].length > 0) {
      report.push('### Console Warnings');
      report.push('');
      errorsByType['console.warn'].forEach((error, index) => {
        report.push(`#### Warning ${index + 1}`);
        report.push(`- **URL:** ${error.url}`);
        report.push(`- **Message:** \`${error.message}\``);
        report.push(`- **Timestamp:** ${error.timestamp}`);
        if (error.screenshot) {
          report.push(`- **Screenshot:** \`${error.screenshot}\``);
        }
        report.push('');
      });
    }

    // Uncaught Exceptions
    if (errorsByType['uncaught'].length > 0) {
      report.push('### Uncaught Exceptions');
      report.push('');
      errorsByType['uncaught'].forEach((error, index) => {
        report.push(`#### Exception ${index + 1}`);
        report.push(`- **URL:** ${error.url}`);
        report.push(`- **Message:** \`${error.message}\``);
        if (error.stack) {
          report.push(`- **Stack Trace:** \`\`\``);
          report.push(error.stack);
          report.push(`\`\`\``);
        }
        report.push(`- **Timestamp:** ${error.timestamp}`);
        if (error.screenshot) {
          report.push(`- **Screenshot:** \`${error.screenshot}\``);
        }
        report.push('');
      });
    }

    // Network Errors
    if (errorsByType['network'].length > 0) {
      report.push('### Network Errors (4xx/5xx)');
      report.push('');
      errorsByType['network'].forEach((error, index) => {
        report.push(`#### Network Error ${index + 1}`);
        report.push(`- **URL:** ${error.url}`);
        report.push(`- **Status Code:** ${error.statusCode}`);
        report.push(`- **Message:** \`${error.message}\``);
        report.push(`- **Timestamp:** ${error.timestamp}`);
        if (error.screenshot) {
          report.push(`- **Screenshot:** \`${error.screenshot}\``);
        }
        report.push('');
      });
    }
  }

  // Recommendations
  report.push('## Recommendations');
  report.push('');

  // Group errors by type for recommendations
  const errorsByType = {
    'console.error': errors.filter(e => e.type === 'console.error'),
    'console.warn': errors.filter(e => e.type === 'console.warn'),
    uncaught: errors.filter(e => e.type === 'uncaught'),
    network: errors.filter(e => e.type === 'network'),
  };

  if (errors.length === 0 && testResults.failed === 0) {
    report.push(' **No issues found!** All tests passed and no errors were detected.');
  } else {
    if (testResults.failed > 0) {
      report.push(`- **${testResults.failed} test(s) failed** - Review failed test details above`);
    }
    if (errorsByType['console.error'].length > 0) {
      report.push(
        `- **${errorsByType['console.error'].length} console error(s)** - Fix JavaScript errors in application code`,
      );
    }
    if (errorsByType['uncaught'].length > 0) {
      report.push(
        `- **${errorsByType['uncaught'].length} uncaught exception(s)** - Add proper error handling and try-catch blocks`,
      );
    }
    if (errorsByType['network'].length > 0) {
      report.push(
        `- **${errorsByType['network'].length} network error(s)** - Check API endpoints and server responses`,
      );
    }
    if (errorsByType['console.warn'].length > 0) {
      report.push(
        `- **${errorsByType['console.warn'].length} console warning(s)** - Review warnings and address non-critical issues`,
      );
    }
  }

  report.push('');
  report.push('---');
  report.push('');
  report.push('*This report was automatically generated by Playwright E2E test suite.*');

  // Write report to file
  const fullPath = join(process.cwd(), outputPath);
  writeFileSync(fullPath, report.join('\n'), 'utf-8');
  console.log(`QA Audit Report generated: ${fullPath}`);
}

/**
 * Parse Playwright test results into summary format
 */
export function parseTestResults(testResults: any[]): TestResultsSummary {
  const summary: TestResultsSummary = {
    total: testResults.length,
    passed: 0,
    failed: 0,
    skipped: 0,
    duration: 0,
    tests: [],
  };

  testResults.forEach(result => {
    summary.duration += result.duration || 0;

    if (result.status === 'passed') {
      summary.passed++;
    } else if (result.status === 'failed') {
      summary.failed++;
    } else if (result.status === 'skipped') {
      summary.skipped++;
    }

    summary.tests.push({
      title: result.title || 'Unknown Test',
      status: result.status || 'unknown',
      duration: result.duration || 0,
      error: result.error?.message,
      steps: result.steps?.map((s: any) => s.title),
    });
  });

  return summary;
}

import { test, expect } from '@playwright/test';
import {
  setupGlobalErrorListener,
  collectPageErrors,
  getCollectedErrors,
  clearCollectedErrors,
  captureErrorScreenshot,
} from '../fixtures/global-error-listener';
import { ensureAuthenticated } from '../fixtures/auth-helper';
import { runMonkeyTest } from '../helpers/monkey-test';

test.describe('Deep Crawl - Page Discovery and Monkey Testing', () => {
  const visitedUrls = new Set<string>();
  const maxPages = 50;
  const maxDepth = 3;

  test.beforeEach(async ({ page }) => {
    clearCollectedErrors();
    await setupGlobalErrorListener(page);
    await ensureAuthenticated(page);
    visitedUrls.clear();
  });

  test.afterEach(async ({ page }) => {
    await collectPageErrors(page);
  });

  test('Crawl all webapp pages and run monkey tests', async ({ page }) => {
    const pagesToVisit: Array<{ url: string; depth: number }> = [{ url: '/webapp', depth: 0 }];
    const crawlResults: Array<{
      url: string;
      depth: number;
      linksFound: number;
      monkeyTestResult?: any;
      errors: number;
    }> = [];

    while (pagesToVisit.length > 0 && visitedUrls.size < maxPages) {
      const current = pagesToVisit.shift();
      if (!current || current.depth > maxDepth) continue;

      const fullUrl = current.url.startsWith('http')
        ? current.url
        : `http://localhost:3000${current.url}`;

      // Skip if already visited
      if (visitedUrls.has(fullUrl)) continue;
      visitedUrls.add(fullUrl);

      try {
        // Navigate to page
        await page.goto(current.url);
        await page.waitForLoadState('networkidle', { timeout: 15000 });
        await collectPageErrors(page);

        // Find all internal links
        const links = await page.locator('a[href^="/webapp"]').all();
        const linkUrls = await Promise.all(
          links.map(async link => {
            const href = await link.getAttribute('href').catch(() => '');
            return href;
          }),
        );

        // Add new links to queue
        linkUrls.forEach(href => {
          if (
            href &&
            !visitedUrls.has(`http://localhost:3000${href}`) &&
            current.depth < maxDepth
          ) {
            pagesToVisit.push({ url: href, depth: current.depth + 1 });
          }
        });

        // Run limited monkey test on this page (5 interactions max per page)
        const monkeyResult = await runMonkeyTest(page, 5);

        // Collect errors for this page
        await collectPageErrors(page);
        const pageErrors = getCollectedErrors().filter(
          e => e.url === page.url() || page.url().includes(e.url),
        );

        crawlResults.push({
          url: current.url,
          depth: current.depth,
          linksFound: linkUrls.length,
          monkeyTestResult: monkeyResult,
          errors: pageErrors.length,
        });

        // Take screenshot if errors found
        if (pageErrors.length > 0) {
          await captureErrorScreenshot(page, pageErrors[0], 'test-failures');
        }

        // Small delay between pages
        await page.waitForTimeout(500);
      } catch (error: any) {
        console.error(`Error crawling ${current.url}:`, error.message);
        crawlResults.push({
          url: current.url,
          depth: current.depth,
          linksFound: 0,
          errors: 1,
        });
      }
    }

    // Log crawl results
    console.log(`\n=== Deep Crawl Results ===`);
    console.log(`Total pages visited: ${crawlResults.length}`);
    console.log(`Total links found: ${crawlResults.reduce((sum, r) => sum + r.linksFound, 0)}`);
    console.log(`Total errors: ${crawlResults.reduce((sum, r) => sum + r.errors, 0)}`);
    console.log(`\nPages with errors:`);
    crawlResults
      .filter(r => r.errors > 0)
      .forEach(r => {
        console.log(`  - ${r.url} (depth ${r.depth}): ${r.errors} error(s)`);
      });

    // Assert that we visited at least some pages
    expect(crawlResults.length).toBeGreaterThan(0);

    // Check for critical errors across all pages
    const allErrors = getCollectedErrors();
    const criticalErrors = allErrors.filter(
      e => e.type === 'uncaught' || (e.type === 'network' && e.statusCode && e.statusCode >= 500),
    );

    if (criticalErrors.length > 0) {
      console.error('Critical errors found during crawl:', criticalErrors);
    }

    // Test passes even with warnings/4xx (they'll be in report)
    // But fail if critical errors found
    expect(criticalErrors.length).toBe(0);
  });
});

