/**
 * Handlers for useConfirm hook.
 */
export function createConfirmHandlers(
  setIsOpen: (open: boolean) => void,
  setResolvePromise: (resolver: ((value: boolean) => void) | null) => void,
  setOptions: (options: any) => void, // justified
  resolvePromise: ((value: boolean) => void) | null,
) {
  return {
    handleConfirm: () => {
      setIsOpen(false);
      if (resolvePromise) {
        resolvePromise(true);
        setResolvePromise(null);
      }
      setTimeout(() => setOptions(null), 200);
    },
    handleCancel: () => {
      setIsOpen(false);
      if (resolvePromise) {
        resolvePromise(false);
        setResolvePromise(null);
      }
      setTimeout(() => setOptions(null), 200);
    },
  };
}

interface GestureState {
  upwardMovement: number;
  dragProgress: number;
  dragY: number;
  velocity: number;
  maxUpwardMovement: number;
}

interface CalculateProgressParams {
  deltaY: number;
  drawerHeight: number;
  threshold: number;
}

interface HandleUpwardGestureParams {
  deltaY: number;
  absDeltaY: number;
  maxUpwardMovementRef: React.MutableRefObject<number | null>;
  onClose: () => void;
}

interface HandleDownwardGestureParams {
  deltaY: number;
  drawerHeight: number;
  threshold: number;
  lastTime: number;
  currentY: number;
  lastY: number;
  currentTime: number;
}

export function calculateDragProgress({
  deltaY,
  drawerHeight,
  threshold,
}: CalculateProgressParams): number {
  return Math.min(1, deltaY / (drawerHeight * threshold));
}
export function handleUpwardGestureAtTop({
  deltaY,
  absDeltaY,
  maxUpwardMovementRef,
  onClose,
}: HandleUpwardGestureParams): {
  shouldClose: boolean;
  upwardMovement: number;
  progress: number;
} {
  const upwardMovementValue = absDeltaY;
  maxUpwardMovementRef.current = Math.max(maxUpwardMovementRef.current ?? 0, upwardMovementValue);
  const progress = Math.min(1, upwardMovementValue / 15);
  const shouldClose = deltaY < -15;

  if (shouldClose) {
    onClose();
  }

  return {
    shouldClose,
    upwardMovement: upwardMovementValue,
    progress,
  };
}
export function handleDownwardGestureAtTop({
  deltaY,
  drawerHeight,
  threshold,
  lastTime,
  currentY,
  lastY,
  currentTime,
}: HandleDownwardGestureParams): {
  progress: number;
  velocity: number;
  shouldPreventDefault: boolean;
} {
  const progress = calculateDragProgress({ deltaY, drawerHeight, threshold });
  const velocity =
    lastTime > 0 && currentTime - lastTime > 0
      ? Math.abs((currentY - lastY) / (currentTime - lastTime))
      : 0;
  const shouldPreventDefault = deltaY > 20;

  return {
    progress: deltaY > 10 && deltaY <= 20 ? progress * 0.6 : progress,
    velocity,
    shouldPreventDefault,
  };
}
export function calculateVelocity(
  currentY: number,
  lastY: number,
  currentTime: number,
  lastTime: number,
): number {
  if (lastTime === 0 || currentTime - lastTime === 0) return 0;
  return Math.abs((currentY - lastY) / (currentTime - lastTime));
}

/**
 * Reusable hook for optimistic mutations with automatic rollback on error.
 * Provides a consistent pattern for optimistic updates across the application.
 */

import { useCallback, useState } from 'react';
import { createMutateHandler } from './useOptimisticMutation/helpers/mutate';

interface UseOptimisticMutationOptions<T> {
  onSuccess?: (data: T) => void;
  onError?: (error: string) => void;
  showSuccessNotification?: (message: string) => void;
  showErrorNotification?: (message: string) => void;
}

interface UseOptimisticMutationReturn<T> {
  mutate: (
    optimisticUpdate: () => void,
    mutationFn: () => Promise<Response>,
    rollbackFn: () => void,
    successMessage?: string,
  ) => Promise<void>;
  isMutating: boolean;
}

/**
 * Hook for optimistic mutations with automatic rollback on error.
 *
 * @template T - Type of data returned from mutation
 * @param {UseOptimisticMutationOptions<T>} options - Configuration options
 * @returns {UseOptimisticMutationReturn<T>} Mutation function and loading state
 *
 * @example
 * ```typescript
 * const { mutate, isMutating } = useOptimisticMutation({
 *   showSuccessNotification: showSuccess,
 *   showErrorNotification: showError,
 * });
 *
 * await mutate(
 *   () => setItems(prev => prev.filter(item => item.id !== id)),
 *   () => fetch(`/api/items/${id}`, { method: 'DELETE' }),
 *   () => setItems(originalItems),
 *   'Item deleted successfully'
 * );
 * ```
 */
export function useOptimisticMutation<T = unknown>(
  options: UseOptimisticMutationOptions<T> = {},
): UseOptimisticMutationReturn<T> {
  const { onSuccess, onError, showSuccessNotification, showErrorNotification } = options;
  const [isMutating, setIsMutating] = useState(false);

  const mutate = useCallback(
    (
      optimisticUpdate: () => void,
      mutationFn: () => Promise<Response>,
      rollbackFn: () => void,
      successMessage?: string,
    ) => {
      const handler = createMutateHandler(
        setIsMutating,
        onSuccess,
        onError,
        showSuccessNotification,
        showErrorNotification,
      );
      return handler(optimisticUpdate, mutationFn, rollbackFn, successMessage);
    },
    [onSuccess, onError, showSuccessNotification, showErrorNotification],
  );

  return { mutate, isMutating };
}

