/**
 * Square Auto-Sync Hook
 * Client-side hook for managing Supabase real-time subscriptions for automatic Square syncing
 */

'use client';

import { useEffect, useRef } from 'react';
import { supabase } from '@/lib/supabase';
import { logger } from '@/lib/logger';
import { useUser } from '@auth0/nextjs-auth0/client';
import { createDebounceSync } from './useSquareAutoSync/helpers/createDebounceSync';
import { setupSquareSubscriptions } from './useSquareAutoSync/helpers/setupSquareSubscriptions';
// Note: This hook triggers syncs via API endpoints, not direct server-side calls
// The actual sync logic is handled server-side via API routes

export interface UseSquareAutoSyncOptions {
  enabled?: boolean; // Override auto-sync enabled check
  debounceMs?: number; // Debounce time for rapid changes
}

/**
 * Hook to set up Square auto-sync subscriptions
 * Listens for changes to employees, dishes, recipes, and ingredients
 */
export function useSquareAutoSync(options: UseSquareAutoSyncOptions = {}) {
  const { user } = useUser();
  // Extract user ID from Auth0 user (user.sub or user.user_id)
  const userId = user?.sub || (user as any)?.user_id; // justified
  const debounceTimersRef = useRef<Map<string, NodeJS.Timeout>>(new Map());

  useEffect(() => {
    if (typeof window === 'undefined') {
      return;
    }

    if (!userId) {
      return;
    }

    if (!supabase) {
      return;
    }

    // Check if auto-sync is enabled (via API)
    let isEnabled = options.enabled;
    if (isEnabled === undefined) {
      // Check server-side via API if not provided
      fetch('/api/square/config')
        .then(res => res.json())
        .then(data => {
          if (data.config) {
            isEnabled = data.config.auto_sync_enabled === true;
          }
        })
        .catch(err => {
          logger.error('[Square Auto-Sync Hook] Error checking auto-sync status:', {
            error: err.message,
            userId,
          });
        });
    }

    if (isEnabled === false) {
      return;
    }

    const debounceDelay = options.debounceMs || 5000;
    // Capture ref value at effect execution time for cleanup
    const timersRef = debounceTimersRef;
    const debounceSync = createDebounceSync({
      debounceTimersRef: timersRef,
      debounceDelay,
      userId,
    });
    const channel = setupSquareSubscriptions({ debounceSync, userId });

    return () => {
      // Cleanup: unsubscribe and clear timers
      channel.unsubscribe();
      const timers = timersRef.current;
      timers.forEach(timer => clearTimeout(timer));
      timers.clear();
      logger.dev('[Square Auto-Sync Hook] Subscriptions cleaned up');
    };
  }, [userId, options.enabled, options.debounceMs]);
}

import { logger } from '@/lib/logger';

interface CreateDebounceSyncParams {
  debounceTimersRef: React.MutableRefObject<Map<string, NodeJS.Timeout>>;
  debounceDelay: number;
  userId?: string;
}

/**
 * Create debounced sync function for Square auto-sync
 */
export function createDebounceSync({
  debounceTimersRef,
  debounceDelay,
  userId,
}: CreateDebounceSyncParams) {
  return (key: string, entityType: string, entityId: string, operation: string) => {
    const existingTimer = debounceTimersRef.current.get(key);
    if (existingTimer) {
      clearTimeout(existingTimer);
    }
    const timer = setTimeout(async () => {
      try {
        await fetch('/api/square/sync/trigger', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            entityType,
            entityId,
            operation,
          }),
        });
      } catch (error: any) { // justified
        logger.error('[Square Auto-Sync Hook] Error in debounced sync:', {
          error: error.message,
          key,
          userId,
        });
      } finally {
        debounceTimersRef.current.delete(key);
      }
    }, debounceDelay);
    debounceTimersRef.current.set(key, timer);
  };
}

'use client';

import { cacheData, getCachedData } from '@/lib/cache/data-cache';
import { logger } from '@/lib/logger';
import { useCallback, useEffect, useState } from 'react';

export interface StaffMember {
  id: string;
  full_name: string;
  role: string | null;
}

/**
 * Hook to fetch active staff members with caching support
 * Returns array of staff members formatted for dropdowns
 */
export function useStaff() {
  const [staff, setStaff] = useState<StaffMember[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<Error | null>(null);

  // Initialize with cached data for instant display
  useEffect(() => {
    const cached = getCachedData<StaffMember[]>('staff_members');
    if (cached) {
      setStaff(cached);
      setLoading(false);
    }
  }, []);

  const fetchStaff = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await fetch('/api/employees?status=active');
      const data = await response.json();

      if (data.success && data.data) {
        // Format staff members for dropdowns: { id, full_name, role }
        const formattedStaff: StaffMember[] = data.data.map((employee: any) => ({ // justified
          id: employee.id,
          full_name: employee.full_name,
          role: employee.role || null,
        }));

        // Sort by full_name for consistent ordering
        formattedStaff.sort((a, b) => a.full_name.localeCompare(b.full_name));

        setStaff(formattedStaff);
        cacheData('staff_members', formattedStaff);
      } else {
        throw new Error(data.error || 'Failed to fetch staff members');
      }
    } catch (err) {
      const error = err instanceof Error ? err : new Error(String(err));
      logger.error('[useStaff] Error fetching staff members:', {
        error: error.message,
        stack: error.stack,
        context: { endpoint: '/api/employees?status=active', operation: 'fetchStaff' },
      });
      setError(error);
    } finally {
      setLoading(false);
    }
  }, []);

  // Fetch fresh data on mount if no cache
  useEffect(() => {
    if (staff.length === 0 && !loading) {
      fetchStaff();
    }
  }, [staff.length, loading, fetchStaff]);

  return {
    staff,
    loading,
    error,
    refetch: fetchStaff,
  };
}

